import { Difficulty } from '../minigames';
import Random from '../util/Random';
import { Edge } from '../globals';
import TILES from '../dungeon/TileMappings';

/**
 * This class manages the battle and combat logic.
 */
export default class Battle {
  /**
   * Creates the battle room
   * @constructor
   * @param {Room} room The room the battle is occurring in
   * @param {Edge} edge The edge the player entered from
   * @param {Phaser.Scene} scene The current scene
   */
  constructor( room, edge, scene ) {
    /**
     * The room the battle is occurring in
     * @type {Room}
     */
    this.room = room;
    
    /**
     * The edge the player entered from
     * @type {Edge}
     */
    this.startEdge = edge;
    
    /**
     * The current scene
     * @type {Phaser.Scene}
     */
    this.scene = scene;
    
    /**
     * The enemy that is currently selected in the battle
     * @type {Enemy}
     */
    this.currentEnemy = null;
    
    /**
     * The index of the currently selected enemy
     * @type {number}
     */
    this.enemyIndex = 0;
    
    /**
     * Whether or not it is the player's turn
     * @type {boolean}
     */
    this.playerTurn = false;
    
    /**
     * Whether or not the battle is active and running
     * @type {boolean}
     */
    this.active = false;
    
    /**
     * Stores information about the minigame currently being played
     * @type {{ game: Phaser.Scene, enemy: Enemy }}
     */
    this.minigame = null;

    // Cheats
    // TODO: DELETE THESE
    this.scene.input.keyboard.createCombo( 'KILL', { deleteOnMatch: true } );
    this.scene.input.keyboard.on( 'keycombomatch', ( event ) => {
      this.slayAll();
    } );
  }

  /**
   * Updates the battle
   */
  update() {
    if ( this.currentEnemy === null ) {
      this.currentEnemy = this.scene.enemyGroup.getChildren()[ 0 ];
      this.enemyIndex = 0;
    }
    else if ( this.playerTurn ) {
      if ( this.scene.keys.down.isDown || this.scene.keys.right.isDown ) {
        this.currentEnemy.deselect();
        this.enemyIndex--;
        if ( this.enemyIndex < 0 ) {
          this.enemyIndex = this.scene.enemyGroup.countActive() - 1;
        }
        this.currentEnemy =
          this.scene.enemyGroup.getChildren()[ this.enemyIndex ];
        this.currentEnemy.select();
        this.scene.input.keyboard.resetKeys();
      }
      else if ( this.scene.keys.up.isDown || this.scene.keys.left.isDown ) {
        this.currentEnemy.deselect();
        this.enemyIndex++;
        if ( this.enemyIndex >= this.scene.enemyGroup.countActive() ) {
          this.enemyIndex = 0;
        }
        this.currentEnemy =
          this.scene.enemyGroup.getChildren()[ this.enemyIndex ];
        this.currentEnemy.select();
        this.scene.input.keyboard.resetKeys();
      }
      else if ( this.scene.keys.space.isDown ) {
        this.playMinigame( this.currentEnemy );
        this.scene.input.keyboard.resetKeys();
      }
    }
  }

  /**
   * Slays all of the enemies
   */
  slayAll() {
    this.currentEnemy = null;
    this.playerTurn = false;

    const enemies = this.scene.enemyGroup.getChildren();
    for ( let i = enemies.length - 1; i >= 0; i-- ) {
      enemies[ i ].injure( enemies[ i ].health );
    }
    this.scene.endCombat();
  }

  /**
   * Begins combat inside of the a battle room
   * @return {Promise} The promise when the battle has been initialized
   */
  begin() {
    this.scene.input.keyboard.resetKeys();
    this.centerCameraToRoom();
    return this.lockRoom()
      .then( () => this.room.spawnEnemies( this.startEdge, this.scene ) )
      .then( () => {
        this.scene.enemyGroup.getChildren().forEach( ( enemy ) => {
          enemy.on( 'pointerdown', () => {
            this.playMinigame( enemy );
          } );
          enemy.on( 'pointerover', () => {
            this.currentEnemy.deselect();
            this.currentEnemy = enemy;
            this.currentEnemy.select();
            this.enemyIndex =
            this.scene.enemyGroup.getChildren().indexOf( enemy );
          } );
        } );
      } )
      .then( () => {
        this.movePlayer();
        this.active = true;
        this.playerTurn = true;
        this.enemyIndex = 0;
        this.currentEnemy = this.scene.enemyGroup
          .getChildren()[ this.enemyIndex ];
        this.currentEnemy.select();
      } );
  }

  /**
   * Ends the combat inside of the battle room
   * @return {Promise} The promise when the battle has ended
   */
  end() {
    this.unlockRoom();
    return this.centerCameraOnPlayer()
      .then( () => {
        this.active = false;
      } );
  }

  /**
   * Moves the player to face enemies
   */
  movePlayer() {
    const callback = () => {
      this.scene.player.movementDisabled = true;
    };
    if ( this.startEdge === Edge.TOP ) {
      this.scene.player.setDestination(
        this.scene.map.tileToWorldX( this.room.centerX + 0.5 ),
        this.scene.map.tileToWorldY(
          this.room.centerY + 0.5 - this.room.height / 4 ),
        callback
      );
    }
    else if ( this.startEdge === Edge.RIGHT ) {
      this.scene.player.setDestination(
        this.scene.map.tileToWorldX(
          this.room.centerX + 0.5 + this.room.width / 4 ),
        this.scene.map.tileToWorldY( this.room.centerY + 0.5 ),
        callback
      );
    }
    else if ( this.startEdge === Edge.BOTTOM ) {
      this.scene.player.setDestination(
        this.scene.map.tileToWorldX( this.room.centerX + 0.5 ),
        this.scene.map.tileToWorldY(
          this.room.centerY + 0.5 + this.room.height / 4 ),
        callback
      );
    }
    else if ( this.startEdge === Edge.LEFT ) {
      this.scene.player.setDestination(
        this.scene.map.tileToWorldX(
          this.room.centerX + 0.5 - this.room.width / 4 ),
        this.scene.map.tileToWorldY( this.room.centerY + 0.5 ),
        callback
      );
    }
  }

  /**
   * Stops player movement and centers the camera in the battle room
   */
  centerCameraToRoom() {
    const duration = 750;
    const { centerX, centerY } = this.room;
    const cX = this.scene.map.tileToWorldX( centerX + 0.5 );
    const cY = this.scene.map.tileToWorldY( centerY + 0.5 );

    this.scene.player.movementDisabled = true;

    const camera = this.scene.cameras.main;
    camera.stopFollow();
    camera.pan( cX, cY, duration, 'Linear' );
  }

  /**
   * Centers the camera on the player
   * @return {Promise} The promise when the camera has been centered
   */
  centerCameraOnPlayer() {
    const duration = 750;
    this.scene.cameras.main.pan( this.scene.player.x, this.scene.player.y,
      duration, 'Linear' );
    return new Promise( ( resolve ) => {
      setTimeout( () => {
        this.scene.cameras.main.startFollow( this.scene.player );
        this.scene.player.movementDisabled = false;
        resolve();
      }, duration );
    } );
  }

  /**
   * Adds the doors to the room to lock the player in for battle
   * @return {Promise} The promise when the room has been locked
   */
  lockRoom() {
    const duration = 750;
    const { x, y, doors } = this.room;
    const camera = this.scene.cameras.main;

    return new Promise( ( resolve, reject ) => {
      setTimeout( () => {
        camera.shake( 200, 0.0025 );
        doors.forEach( ( door ) => {
          // Top or Bottom
          if ( door.edge === Edge.TOP || door.edge === Edge.BOTTOM ) {
            this.scene.stuffLayer.putTilesAt( TILES.GATE.HORIZONTAL,
              x + door.x - 1, y + door.y );
          }
          // Left or Right
          else if ( door.edge === Edge.LEFT || door.edge === Edge.RIGHT ) {
            this.scene.stuffLayer.putTilesAt( TILES.GATE.VERTICAL,
              x + door.x, y + door.y - 1 );
          }
        } );
        this.scene.stuffCollider.update();
        this.scene.player.movementDisabled = false;
        resolve();
      }, duration );
    } );
  }

  /**
   * Removes the doors from the room
   */
  unlockRoom() {
    const { x, y, doors } = this.room;
    doors.forEach( ( door ) => {
      // Top or Bottom
      if ( door.edge === Edge.TOP || door.edge === Edge.BOTTOM ) {
        this.scene.stuffLayer.removeTileAt( x + door.x - 1, y + door.y );
        this.scene.stuffLayer.removeTileAt( x + door.x, y + door.y );
        this.scene.stuffLayer.removeTileAt( x + door.x + 1, y + door.y );
      }
      // Left or Right
      else if ( door.edge === Edge.LEFT || door.edge === Edge.RIGHT ) {
        this.scene.stuffLayer.removeTileAt( x + door.x, y + door.y - 1 );
        this.scene.stuffLayer.removeTileAt( x + door.x, y + door.y );
      }
    } );
  }

  /**
   * Opens the given mini game and pauses the game until mini game is over
   * @param {Enemy} enemy The enemy spawning the mini game
   * @param {number} difficulty The difficulty of the mini game
   */
  playMinigame( enemy, difficulty = Difficulty.EASY ) {
    if ( !this.playerTurn ) {
      return;
    }

    const name = enemy.getRandomMiniGame();
    this.minigame = {
      game: this.scene.scene.get( name ),
      enemy: enemy
    };
    if ( !this.minigame.game ) {
      return;
    }

    this.scene.scene
      .launch( name, {
        parent: this,
        difficulty: difficulty
      } )
      .bringToTop( name )
      .pause();
  }

  /**
   * Stops the current mini game and resumes the game
   * @param {object} result The result of the mini game
   */
  continueCombat( result ) {
    const { win, damage } = result;

    this.scene.scene.stop( this.minigame.game.key );
    this.scene.input.keyboard.resetKeys();
    this.scene.scene.resume();

    // Win
    if ( win ) {
      this.scene.player.attack( this.minigame.enemy, damage );
      if ( this.scene.enemyGroup.countActive() === 0 ) {
        this.scene.endCombat();
        this.playerTurn = false;
      }
      else {
        this.playerTurn = true;
        if ( !this.minigame.enemy.active ) {
          setTimeout( () => {
            this.enemyIndex = 0;
            this.currentEnemy = this.scene.enemyGroup
              .getChildren()[ this.enemyIndex ];
            this.currentEnemy.select();
          }, 200 );
        }
      }
    }
    // Lose
    else {
      // Enemies attack
      this.playerTurn = false;
      this.performEnemyAttack( this.scene.enemyGroup.getChildren(), 0 )
        .then( () => {
          this.playerTurn = true;
        } );
    }
  }

  /**
   * Performs an enemy attack
   * @param {Enemy[]} enemies The enemies attacking
   * @param {number} index The index of the attacking enemy
   * @return {Promise} The promise when the enemy is done attacking
   */
  performEnemyAttack( enemies, index ) {
    this.scene.cameras.main.shake( 200, 0.0025 );
    this.scene.cameras.main.flash( 200, 150, 0, 0 );

    const r = new Random();
    const damage = r.randInt( 1, 3 );
    enemies[ index ].attack( this.scene.player, damage );
    // Player is dead
    if ( !this.scene.player.alive ) {
      this.scene.gameOver();
      return Promise.resolve();
    }

    // Wait 750ms, then have next enemy attack #Recursion
    return new Promise( ( resolve ) => {
      setTimeout( () => {
        // No more enemies
        if ( index === enemies.length - 1 ) {
          resolve();
        }
        // Next enemy attack
        else {
          this.performEnemyAttack( enemies, index + 1 )
            .then( () => resolve() );
        }
      }, 750 );
    } );
  }
}

import Random from '../util/Random';
import { BugSpray, Injection, Timelapse } from '../entity/items';

/**
 * Contains methods for dropping items during / after battle
 */
export default class BattleDrop {
  /**
   * @param {number} x The x-position of the item
   * @param {number} y The y-position of the item
   * @param {Phaser.Scene} scene The current scene
   */
  static drop( x, y, scene ) {
    const r = new Random( new Date().getTime() );
    const num = r.randInt( 0, 100 );

    // 40% injection
    if ( num < 40 ) {
      new Injection( x, y, scene );
    }
    // 10% timelapse
    else if ( num >= 40 && num < 50 ) {
      new Timelapse( x, y, scene );
    }
    // 15% bug spray
    else if ( num >= 50 && num < 65 ) {
      new BugSpray( x, y, scene );
    }
  }
}

/**
 * The health bar is a generic bar to display the health of an entity
 */
export default class HealthBar {
  /**
   * @constructor
   * @param {Phaser.Scene} scene The current scene
   * @param {HealthBarConfig} config The altered config
   */
  constructor( scene, config ) {
    /**
     * The current scene
     * @type {Phaser.Scene}
     */
    this.scene = scene;
    
    /**
     * The configuration for the health bar
     * @type {HealthBarConfig}
     */
    this.config = this._mergeWithDefaultConfig( config );
    
    /**
     * The horizontal position in the world
     * @type {number}
     */
    this.x = 0;
    
    /**
     * The vertical position in the world
     * @type {number}
     */
    this.y = 0;
    
    /**
     * The border graphic around the health bar
     * @private
     * @type {Phaser.GameObjects.Rectangle}
     */
    this._border = null;
    
    /**
     * The background graphic behind the health bar
     * @private
     * @type {Phaser.GameObjects.Rectangle}
     */
    this._background = null;
    
    /**
     * The bar graphic to display the health
     * @private
     * @type {Phaser.GameObjects.Rectangle}
     */
    this._bar = null;
    
    this.setPosition( this.config.x, this.config.y );
    this._drawBackground();
    this._drawBorder();
    this._drawHealthBar();
  }

  /**
   * Merges the given config with the defaults
   * @private
   * @param {HealthBarConfig} config The given config
   * @return {HealthBarConfig} the merged config object
   */
  _mergeWithDefaultConfig( config ) {
    const defaultConfig = {
      width: 250,
      height: 40,
      x: 0,
      y: 0,
      backgroundColor: 0x651828,
      barColor: 0xFF0000,
      border: {
        color: 0x000000,
        width: 1
      },
      animationDuration: 200
    };
    return Object.assign( {}, defaultConfig, config );
  }

  /**
   * Draws the border of the health bar
   * @private
   */
  _drawBorder() {
    const { x, y, width, height } = this.config;
    const bw = this.config.border.width;
    this._border = this.scene.add.rectangle( x - bw, y - bw,
      width + bw * 2, height + bw * 2 );
    this._border.setStrokeStyle( this.config.border.color );
    this._border.lineWidth = bw;
    this._border.setOrigin( 0, 0 );
  }

  /**
   * Draws the background of the health bar
   * @private
   */
  _drawBackground() {
    const { x, y, width, height } = this.config;
    this._background = this.scene.add.rectangle( x, y, width, height,
      this.config.backgroundColor );
    this._background.setOrigin( 0, 0 );
  }

  /**
   * Draws the health bar
   * @private
   */
  _drawHealthBar() {
    const { x, y, width, height } = this.config;
    this._bar = this.scene.add.rectangle( x, y, width, height,
      this.config.barColor );
    this._bar.setOrigin( 0, 0 );
  }

  /**
   * Destroys the health bar
   */
  destroy() {
    this._bar.destroy();
    this._border.destroy();
    this._background.destroy();
  }

  /**
   * Sets the health bar percentage
   * @param {number} percent The percentage
   */
  setPercent( percent ) {
    if ( percent < 0 ) {
      percent = 0;
    }
    else if ( percent > 1 ) {
      percent = 1;
    }
    this.setWidth( percent * this.config.width );
  }

  /**
   * Sets the width of the bar
   * @param {number} width The bar width
   */
  setWidth( width ) {
    this.scene.add.tween( {
      targets: this._bar,
      duration: this.config.animationDuration,
      width: width,
      ease: 'Linear'
    } );
  }

  /**
   * Sets the bar color
   * @param {number} color The new color
   */
  setBarColor( color ) {
    if ( !this._bar ) {
      return;
    }

    this._bar.fillColor = color;
  }

  /**
   * Sets the position of the health bar
   * @param {number} x The x-coordinate
   * @param {number} y The y-coordinate
   */
  setPosition( x, y ) {
    this.x = x;
    this.y = y;

    if ( this._border && this._background && this._bar ) {
      this._border.setPosition( x, y );
      this._background.setPosition( x, y );
      this._bar.setPosition( x, y );
    }
  }
}

import Tiles from './Tiles';

/**
 * Logs the dungeon map to the console in a colored table structure
 * @param {Dungeon} dungeon The dungeon to map
 * @param {object} config The debug map configuration
 */
export function debugMap( dungeon, config = {} ) {
  config = Object.assign(
    {},
    {
      empty: ' ',
      emptyColor: 'rgb(0, 0, 0)',
      wall: '#',
      wallColor: 'rgb(255, 0, 0)',
      floor: '=',
      floorColor: 'rgb(210, 210, 210)',
      door: '.',
      doorColor: 'rgb(0, 0, 255)',
      start: 'S',
      end: 'E',
      battle: 'I',
      startColor: 'rgb(0, 0, 0)',
      fontSize: '15px'
    },
    config
  );

  let string = '';
  const styles = [];

  // First line in the browser console window has console line mapping
  // (e.g. "dungeon.js:724") which throws off the table. Kill two birds by
  // displaying a guide on the first two lines.
  string += 'Dungeon: the console window should be big enough to see all of '
    + 'the guide on the next line:\n';
  string += `%c|${'='.repeat( dungeon.width * 2 - 2 )}|\n\n`;
  styles.push( `font-size: ${config.fontSize}` );

  for ( let y = 0; y < dungeon.height; y += 1 ) {
    for ( let x = 0; x < dungeon.width; x += 1 ) {
      const tile = dungeon.tiles[ y ][ x ];
      if ( tile === Tiles.EMPTY ) {
        string += `%c${config.empty}`;
        styles.push(
          `color: ${config.emptyColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.WALL ) {
        string += `%c${config.wall}`;
        styles.push(
          `color: ${config.wallColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.FLOOR ) {
        string += `%c${config.floor}`;
        styles.push(
          `color: ${config.floorColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.DOOR ) {
        string += `%c${config.door}`;
        styles.push(
          `color: ${config.doorColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.START ) {
        string += `%c${config.start}`;
        styles.push(
          `color: ${config.startColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.END ) {
        string += `%c${config.end}`;
        styles.push(
          `color: ${config.startColor}; font-size: ${config.fontSize}` );
      }
      else if ( tile === Tiles.ITEM ) {
        string += `%c${config.battle}`;
        styles.push(
          `color: ${config.startColor}; font-size: ${config.fontSize}` );
      }
      string += ' ';
    }
    string += '\n';
  }
  console.log( string, ...styles );
}

import Random from '../util/Random';
import { Room, RoomType } from './Room';
import Tiles from './Tiles';
import Hallway from './Hallway';
import { Direction } from '../globals';

/**
 * The default config for the dungeon
 * @type {DungeonConfig}
 */
const baseConfig = {
  width: 55,
  height: 55,
  randomSeed: undefined,
  doorPadding: 1,
  rooms: {
    width: {
      min: 7,
      max: 21,
      even: false
    },
    height: {
      min: 7,
      max: 17,
      even: false
    },
    maxArea: 250,
    maxRooms: 50
  }
};

/**
 * The minimum length of a hallway between two rooms
 * @type {number}
 */
const MIN_HALL_LENGTH = 2;

/**
 * The maximum length of a hallway between two rooms
 * @type {number}
 */
const MAX_HALL_LENGTH = 8;

/**
 * The minimum width of a room regardless of the dungeon's config
 * @type {number}
 */
const MIN_WIDTH = 3;

/**
 * The minimum height of a room regardless of the dungeon's config
 * @type {number}
 */
const MIN_HEIGHT = 3;

/**
 * The number of times to attempt placing a room in the dungeon
 * @type {number}
 */
const MAX_RETRY_COUNT = 150;

/**
 * The dungeon class to generate dungeon rooms and positions
 */
export default class Dungeon {
  /**
   * @constructor
   * @param {DungeonConfig} config
   */
  constructor( config = {} ) {
    const rooms = config.rooms || {};
    rooms.width = Object.assign( {}, baseConfig.rooms.width, rooms.width );
    rooms.height = Object.assign( {}, baseConfig.rooms.height, rooms.height );
    rooms.maxArea = rooms.maxArea || baseConfig.rooms.maxArea;
    rooms.maxRooms = rooms.maxRooms || baseConfig.rooms.maxRooms;

    // Clamp room size
    rooms.width.min = Math.max( MIN_WIDTH, rooms.width.min );
    rooms.height.min = Math.max( MIN_HEIGHT, rooms.height.min );
    rooms.width.max = Math.max( rooms.width.max, rooms.width.min );
    rooms.height.max = Math.max( rooms.height.max, rooms.height.min );

    // Avoid an impossibly small maxArea
    const minArea = rooms.width.min * rooms.height.min;
    rooms.maxArea = Math.max( rooms.maxArea, minArea );

    /**
     * The number of tiles required on each side of a door
     * @type {number}
     */
    this.doorPadding = config.doorPadding || baseConfig.doorPadding;
    
    /**
     * The width of the dungeon in tiles
     * @type {number}
     */
    this.width = config.width || baseConfig.width;
    
    /**
     * The height of the dungeon in tiles
     * @type {number}
     */
    this.height = config.height || baseConfig.height;
    
    /**
     * The configuration for room generation
     * @type {RoomConfig}
     */
    this.roomConfig = rooms;

    /**
     * The list of rooms in the dungeon
     * @type {Array.<Room>}
     */
    this.rooms = [];
    
    /**
     * The random number generator
     * @private
     * @type {Random}
     */
    this.r = new Random( config.randomSeed );

    /**
     * 2D grid matching map dimensions where every element contains an array of
     * all the rooms in that location
     * @type {Array.<Array.<Array.<Room>>>}
     */
    this.roomGrid = [];

    /**
     * A map of the connections between rooms and hallways
     * @type {Object}
     */
    this.connections = {};
    
    /**
     * The starting room for the dungeon
     * @type {Room}
     */
    this.startRoom = null;

    this.generate();
    
    /**
     * The full map of the dungeon
     * @type {Array.<Array.<Tiles>>}
     */
    this.tiles = this.getTiles();
    
    this.rooms.forEach( ( room ) => room.updateDoorPositions() );
  }

  /**
   * Generates the dungeon and populates it with rooms and hallways
   */
  generate() {
    // Populate empty 2D array
    for ( let r = 0; r < this.height; r++ ) {
      this.roomGrid.push( [] );
      for ( let c = 0; c < this.width; c++ ) {
        this.roomGrid[ r ].push( [] );
      }
    }

    // Seed the map with a starting randomly sized room in the center of the map
    const room = this.createRandomRoom();
    room.setPosition(
      Math.floor( this.width / 2 ) - Math.floor( room.width / 2 ),
      Math.floor( this.height / 2 ) - Math.floor( room.height / 2 )
    );
    this.addRoom( room );
    this.connections[ room.id ] = [];

    // Continue generating rooms until we hit the cap or have hit the maximum
    // iterations (usually due to not being able to fit more rooms)
    let i = this.roomConfig.maxRooms * 5;
    while ( this.rooms.length < this.roomConfig.maxRooms && i > 0 ) {
      this.generateRoom();
      i -= 1;
    }

    // Try to make 2 more random hallways
    this.generateRandomHallways();

    // Select item rooms
    this.selectItemRooms();

    // Pick the starting room
    this.selectStartRoom();

    // Pick the ending room
    this.selectEndRoom();
  }

  /**
   * Creates a room of random width and height
   * @private
   * @return {Room} the created room
   */
  createRandomRoom() {
    let width = 0;
    let height = 0;
    let area = 0;

    // Find width and height using min/max sizes while keeping under maxArea
    const config = this.roomConfig;
    do {
      width = this.r.randInt( config.width.min, config.width.max,
        config.width.even );
      height = this.r.randInt( config.height.min, config.height.max,
        config.height.even );
      area = width * height;
    } while ( area > config.maxArea );

    return new Room( width, height );
  }

  /**
   * Creates a new room and tries to add it to the dungeon
   * @private
   */
  generateRoom() {
    const room = this.createRandomRoom();

    let i = MAX_RETRY_COUNT;
    while ( i > 0 ) {
      // Attempt to find another room to attach this one to
      const result = this.findRoomAttachment( room );
      room.setPosition( result.x, result.y );

      // Try to add it. If successful, add the door and hallway between
      if ( this.addRoom( room ) ) {
        const [ door1, door2 ] =
          this.findNewDoorLocation( room, result );

        // Try to add hallway, if unsuccessful, remove room
        if ( this.addHallway( door1, door2, result.dist ) ) {
          this.connections[ room.id ] = [ result.target.id ];
          this.connections[ result.target.id ].push( room.id );

          this.addDoor( door1 );
          this.addDoor( door2 );
          break;
        }
        else {
          this.removeRoom( room );
        }
      }

      i -= 1;
    }
  }

  /**
   * Generates two random hallways between two random rooms to try and make a
   * loop in the dungeon.
   * @private
   */
  generateRandomHallways() {
    let count = 0;
    let retries = MAX_RETRY_COUNT;

    do {
      retries--;

      /** @type Room */
      const room1 = this.r.randPick(
        this.rooms.filter( ( room ) => !( room instanceof Hallway ) )
      );

      /** @type Room */
      const room2 = this.r.randPick(
        this.rooms.filter(
          ( room ) => !( room instanceof Hallway ) && room.id !== room1.id )
      );

      // Prevent multiple connections between the same room
      if ( this.connections[ room1.id ].indexOf( room2.id ) >= 0 ) {
        continue;
      }

      // Get the direction of the hallway
      let direction;
      if ( room1.overlapsX( room2 ) ) {
        if ( room1.top < room2.top ) {
          direction = Direction.NORTH;
        }
        else {
          direction = Direction.SOUTH;
        }
      }
      else if ( room1.overlapsY( room2 ) ) {
        if ( room1.left < room2.left ) {
          direction = Direction.WEST;
        }
        else {
          direction = Direction.EAST;
        }
      }
      else {
        // No hallway possible
        continue;
      }

      const [ door1, door2 ] = this.findNewDoorLocation( room1,
        {
          target: room2,
          direction: direction
        } );
      const dist = Math.sqrt( Math.pow( door1.x - door2.x, 2 ) +
        Math.pow( door1.y - door2.y, 2 ) );

      // Make sure the doors are not on the corners of the room
      if ( room1.isCorner( door1 ) || room2.isCorner( door2 ) ) {
        continue;
      }

      // Try to add the hallway
      if ( this.addHallway( door1, door2, dist - 1 ) ) {
        this.connections[ room1.id ].push( room2.id );
        this.connections[ room2.id ].push( room1.id );

        this.addDoor( door1 );
        this.addDoor( door2 );
        count++;
      }
    } while ( retries > 0 && count < 3 );
  }

  /**
   * Selects the rooms in the dungeon that are small and places items in them
   * @private
   */
  selectItemRooms() {
    this.rooms
      .filter( ( room ) => room.type !== RoomType.HALLWAY )
      .sort( ( a, b ) => a.area < b.area ? -1 : ( a.area === b.area ? 0 : 1 ) )
      .slice( 0, 5 )
      .forEach( ( room ) => {
        room.type = RoomType.ITEM;
      } );
  }

  /**
   * Selects the starting room
   * @private
   */
  selectStartRoom() {
    /**
     * The starting room
     * @type Room
     */
    let sRoom;
    do {
      sRoom = this.r.randPick(
        this.rooms.filter( ( room ) => !( room instanceof Hallway ) )
      );
    } while ( this.connections[ sRoom.id ].length < 3 );
    sRoom.type = RoomType.START;
    sRoom.tiles[ sRoom.centerY - sRoom.y ][ sRoom.centerX - sRoom.x ]
      = Tiles.START; // TODO: remove, for visualizations
    this.startRoom = sRoom;
  }

  /**
   * Selects the ending room
   * @private
   */
  selectEndRoom() {
    /**
     * The end room
     * @type Room
     */
    let eRoom;
    do {
      eRoom = this.r.randPick(
        this.rooms.filter( ( room ) => !( room instanceof Hallway ) )
      );
    } while ( this.connections[ eRoom.id ].length >= 2 );
    eRoom.type = RoomType.START;
    eRoom.tiles[ eRoom.centerY - eRoom.y ][ eRoom.centerX - eRoom.x ]
      = Tiles.END; // TODO: remove, for visualizations
  }

  /**
   * Adds a room to the current dungeon
   * @private
   * @param {Room} room The room to add
   * @return {boolean} True if the room was successfully added
   */
  addRoom( room ) {
    // if the room won't fit, we don't add it
    if ( !this.canFitRoom( room ) ) {
      return false;
    }

    room.id = this.rooms.length;
    this.rooms.push( room );

    // Update all tiles in the roomGrid to indicate that this room is sitting
    // on them.
    for ( let r = room.top; r <= room.bottom; r++ ) {
      for ( let c = room.left; c <= room.right; c++ ) {
        this.roomGrid[ r ][ c ].push( room );
      }
    }
    return true;
  }

  /**
   * Removes a room from the current dungeon
   * @private
   * @param {Room} room The room to remove
   */
  removeRoom( room ) {
    this.rooms = this.rooms.filter( ( r ) => r !== room );
    for ( let r = room.top; r <= room.bottom; r++ ) {
      for ( let c = room.left; c <= room.right; c++ ) {
        this.roomGrid[ r ][ c ] =
          this.roomGrid[ r ][ c ].filter( ( r ) => r !== room );
      }
    }
  }

  /**
   * Adds a door between rooms in a dungeon
   * @private
   * @param {TilePosition} position The position of the door
   */
  addDoor( position ) {
    // Get all the rooms at the location of the door
    const rooms = this.roomGrid[ position.y ][ position.x ];
    for ( const room of rooms ) {
      // convert the door position from world space to room space
      const x = position.x - room.x;
      const y = position.y - room.y;

      // set the tile to be a door
      room.tiles[ y ][ x ] = Tiles.DOOR;
    }
  }

  /**
   * Adds a hallway between two doors in the dungeon
   * @private
   * @param {TilePosition} door1 The first door
   * @param {TilePosition} door2 The second door
   * @param {number} dist The distance between the two doors
   * @return {boolean} True if the hallway could be added (or no hallway)
   */
  addHallway( door1, door2, dist ) {
    if ( dist < MIN_HALL_LENGTH ) {
      return false;
    }

    // No hallway if doors are next to each other
    if ( dist === 0 ) {
      return true;
    }
    return this.addRoom( Hallway.fromDoors( door1, door2 ) );
  }

  /**
   * Finds a room that the given room can be placed next to
   * @private
   * @param {Room} room The room to place
   * @return {RoomAttachment} The position for the new room and target room
   */
  findRoomAttachment( room ) {
    // Pick a random room (filter out the hallways)
    const r = this.r.randPick(
      this.rooms.filter( ( room ) => !( room instanceof Hallway ) )
    );

    let x = 0;
    let y = 0;
    const pad = 2 * this.doorPadding;

    // The distance between the rooms
    const dist = this.r.randInt( MIN_HALL_LENGTH, MAX_HALL_LENGTH );
    const direction = this.r.randDirection();

    // Randomly position this room on one of the sides of the random room
    switch ( direction ) {
      case Direction.NORTH:
        x = this.r.randInt( r.left - ( room.width - 1 ) + pad, r.right - pad );
        y = r.top - room.height - dist;
        break;
      case Direction.WEST:
        x = r.left - room.width - dist;
        y = this.r.randInt( r.top - ( room.height - 1 ) + pad, r.bottom - pad );
        break;
      case Direction.EAST:
        x = r.right + 1 + dist;
        y = this.r.randInt( r.top - ( room.height - 1 ) + pad, r.bottom - pad );
        break;
      case Direction.SOUTH:
        x = this.r.randInt( r.left - ( room.width - 1 ) + pad, r.right - pad );
        y = r.bottom + 1 + dist;
        break;
    }

    return {
      x: x,
      y: y,
      target: r,
      dist: dist,
      direction: direction
    };
  }

  /**
   * Gets the location for the two new doors
   * @private
   * @param {Room} room1 The first room
   * @param {{target: Room, direction: Direction}} obj The second room and
   *          direction
   * @return {Array.<TilePosition>} an array of door positions
   */
  findNewDoorLocation( room1, { target: room2, direction } ) {
    const door1 = {
      x: -1,
      y: -1
    };
    const door2 = {
      x: -1,
      y: -1
    };

    if ( Direction.NORTH === direction ) {
      door1.x = door2.x = this.r.randInt(
        Math.floor( Math.max( room2.left, room1.left ) + this.doorPadding ),
        Math.floor( Math.min( room2.right, room1.right ) - this.doorPadding )
      );
      door1.y = room1.y + room1.height - 1;
      door2.y = room2.y;
    }
    else if ( Direction.WEST === direction ) {
      door1.x = room1.x + room1.width - 1;
      door2.x = room2.x;
      door1.y = door2.y = this.r.randInt(
        Math.floor( Math.max( room2.top, room1.top ) + this.doorPadding ),
        Math.floor( Math.min( room2.bottom, room1.bottom ) - this.doorPadding )
      );
    }
    else if ( Direction.EAST === direction ) {
      // East
      door1.x = room1.x;
      door2.x = room2.x + room2.width - 1;
      door1.y = door2.y = this.r.randInt(
        Math.floor( Math.max( room2.top, room1.top ) + this.doorPadding ),
        Math.floor( Math.min( room2.bottom, room1.bottom ) - this.doorPadding )
      );
    }
    else if ( Direction.SOUTH === direction ) {
      // South
      door1.x = door2.x = this.r.randInt(
        Math.floor( Math.max( room2.left, room1.left ) + this.doorPadding ),
        Math.floor( Math.min( room2.right, room1.right ) - this.doorPadding )
      );
      door1.y = room1.y;
      door2.y = room2.y + room2.height - 1;
    }

    return [ door1, door2 ];
  }

  /**
   * Checks to see if the given room can fit inside the dungeon
   * @private
   * @param {Room} room The room to check
   * @return {boolean} True if the room can fit in the dungeon
   */
  canFitRoom( room ) {
    // Make sure the room fits inside the dungeon
    if ( room.x < 0 || room.x + room.width > this.width - 1 ) {
      return false;
    }
    if ( room.y < 0 || room.y + room.height > this.height - 1 ) {
      return false;
    }

    // Make sure this room doesn't intersect any existing rooms
    for ( let i = 0; i < this.rooms.length; i++ ) {
      if ( room.overlaps( this.rooms[ i ] ) ) {
        return false;
      }
    }
    return true;
  }

  /**
   * Gets the full map of the dungeon
   * @return {Array.<Array.<Tiles>>} 2D map of the dungeon
   */
  getTiles() {
    const tiles = Array( this.height );
    for ( let r = 0; r < this.height; r++ ) {
      tiles[ r ] = Array( this.width );
      for ( let c = 0; c < this.width; c++ ) {
        tiles[ r ][ c ] = Tiles.EMPTY;
      }
    }

    // Fill in the map with details from each room
    for ( const room of this.rooms ) {
      for ( let r = 0; r < room.height; r++ ) {
        for ( let c = 0; c < room.width; c++ ) {
          tiles[ r + room.y ][ c + room.x ] = room.tiles[ r ][ c ];
        }
      }
    }

    return tiles;
  }

  /**
   * Gets the room at the specified location
   * @param {number} x The horizontal tile position
   * @param {number} y The vertical tile position
   * @return {Room|null} The room at the coordinate
   */
  getRoomAt( x, y ) {
    if ( x < 0 || y < 0 || x >= this.width || y >= this.height ) {
      return null;
    }
    return this.roomGrid[ y ][ x ][ 0 ]; // Assumes 1 room per tile
  }
}

import { Room, RoomType } from './Room';
import Tiles from './Tiles';
import { Orientation } from '../globals';

/**
 * A hallway is a simplified room that is used as a connector between two other
 * rooms.
 */
export default class Hallway extends Room {
  /**
   * @constructor
   * @param {number} width The width of the hallway
   * @param {number} height The height of the hallway
   */
  constructor( width, height ) {
    super( width, height );
    
    /**
     * The type of room
     * @type {RoomType}
     */
    this.type = RoomType.HALLWAY;
  }

  /**
   * Creates a Hallway from the given two doors
   * @param {TilePosition} door1 The first door
   * @param {TilePosition} door2 The second door
   * @return {Hallway} The constructed hallway
   */
  static fromDoors( door1, door2 ) {
    let hallway;
    if ( door1.x === door2.x ) { // Vertical Hallway
      hallway = new Hallway( 3, Math.abs( door1.y - door2.y ) - 1 );
      hallway.setPosition( door1.x - 1, Math.min( door1.y, door2.y ) + 1 );

      for ( let r = 0; r < hallway.height; r++ ) {
        hallway.tiles[ r ][ 1 ] = Tiles.FLOOR;
      }

      hallway.orientation = Orientation.VERTICAL;
    }
    else if ( door1.y === door2.y ) { // Horizontal Hallway
      hallway = new Hallway( Math.abs( door1.x - door2.x ) - 1, 3 );
      hallway.setPosition( Math.min( door1.x, door2.x ) + 1, door1.y - 1 );

      for ( let c = 0; c < hallway.width; c++ ) {
        hallway.tiles[ 1 ][ c ] = Tiles.FLOOR;
      }

      hallway.orientation = Orientation.HORIZONTAL;
    }
    return hallway;
  }
}

import Tiles from './Tiles';
import { Edge, RoomType } from '../globals';
import { Enemy } from '../entity';
import ItemDrop from '../entity/items/ItemDrop';

/**
 * Describes how enemies will spawn based on the entry direction of the player
 * @type {Map.<Direction,Map.<number, object>>}
 */
const enemySpawnMap = {
  0: {
    2: {
      start: Math.PI / 4,
      inc: Math.PI / 2
    },
    3: {
      start: Math.PI / 4,
      inc: Math.PI / 4
    },
    4: {
      start: Math.PI / 8,
      inc: Math.PI / 4
    }
  },
  1: {
    2: {
      start: 3 * Math.PI / 4,
      inc: Math.PI / 2
    },
    3: {
      start: 3 * Math.PI / 4,
      inc: Math.PI / 4
    },
    4: {
      start: 5 * Math.PI / 8,
      inc: Math.PI / 4
    }
  },
  2: {
    2: {
      start: -Math.PI / 4,
      inc: -Math.PI / 2
    },
    3: {
      start: -Math.PI / 4,
      inc: -Math.PI / 4
    },
    4: {
      start: -Math.PI / 8,
      inc: -Math.PI / 4
    }
  },
  3: {
    2: {
      start: Math.PI / 4,
      inc: -Math.PI / 2
    },
    3: {
      start: Math.PI / 4,
      inc: -Math.PI / 4
    },
    4: {
      start: 3 * Math.PI / 8,
      inc: -Math.PI / 4
    }
  }
};

/**
 * Each room in a dungeon has walls, floor, and doors. Also entities live within
 * these rooms. The entities defined for each room will be random and depend on
 * the level.
 */
class Room {
  /**
   * @constructor
   * @param {number} width The width of the room
   * @param {number} height The height of the room
   */
  constructor( width, height ) {
    /**
     * The width of the room in tiles
     * @type {number}
     */
    this.width = width;
    
    /**
     * The height of the room in tiles
     * @type {number}
     */
    this.height = height;
    
    /**
     * The area of the room in tiles-squared
     * @type {number}
     */
    this.area = width * height;

    this.setPosition( 0, 0 );

    /**
     * The ID number of the room
     * @type {number}
     */
    this.id = -1;
    
    /**
     * The room type
     * @type {RoomType}
     */
    this.type = RoomType.BATTLE;
    
    /**
     * Whether or not the player has entered this room
     * @type {boolean}
     */
    this.entered = false;

    /**
     * The doors in the room
     * @type {Array.<Door>}
     */
    this.doors = [];
    
    /**
     * The tiles in the room
     * @type {Array.<Tiles>}
     */
    this.tiles = [];

    // Surround the room with walls, and fill the rest with floors.
    for ( let r = 0; r < this.height; r++ ) {
      const row = [];
      for ( let c = 0; c < this.width; c++ ) {
        if ( r === 0 || r === this.height - 1 ||
          c === 0 || c === this.width - 1 ) {
          row.push( Tiles.WALL );
        }
        else {
          row.push( Tiles.FLOOR );
        }
      }
      this.tiles.push( row );
    }
  }

  /**
   * Sets the position of the room in the dungeon. Also updates positional
   * variables in the room.
   * @param {number} x The x position of the room
   * @param {number} y The y position of the room
   */
  setPosition( x, y ) {
    /**
     * The horizontal position in tiles
     * @type {number}
     */
    this.x = x;
    
    /**
     * The vertical position in tiles
     * @type {number}
     */
    this.y = y;
    
    /**
     * The position of the left edge of the room
     * @type {number}
     */
    this.left = x;

    /**
     * The position of the right edge of the room
     * @type {number}
     */
    this.right = x + this.width - 1;
    
    /**
     * The position of the top edge of the room
     * @type {number}
     */
    this.top = y;
    
    /**
     * The position of the bottom edge of the room
     * @type {number}
     */
    this.bottom = y + this.height - 1;
    
    /**
     * The horizontal center position in tiles
     * @type {number}
     */
    this.centerX = x + Math.floor( this.width / 2 );
    
    /**
     * The vertical center position in tiles
     * @type {number}
     */
    this.centerY = y + Math.floor( this.height / 2 );
  }

  /**
   * Finds all the doors in the room and returns a list of their positions
   */
  updateDoorPositions() {
    this.doors = [];
    for ( let y = 0; y < this.height; y++ ) {
      for ( let x = 0; x < this.width; x++ ) {
        if ( this.tiles[ y ][ x ] === Tiles.DOOR ) {
          let edge = Edge.NONE;
          if ( y === 0 ) {
            edge = Edge.TOP;
          }
          else if ( y === this.height - 1 ) {
            edge = Edge.BOTTOM;
          }
          else if ( x === 0 ) {
            edge = Edge.LEFT;
          }
          else if ( x === this.width - 1 ) {
            edge = Edge.RIGHT;
          }
          this.doors.push( {
            x: x,
            y: y,
            edge: edge
          } );
        }
      }
    }
  }

  /**
   * Checks if the current room overlaps the given room
   * @param {Room} room The room to check overlap with
   * @return {boolean} True if the rooms overlap
   */
  overlaps( room ) {
    return this.overlapsX( room ) && this.overlapsY( room );
  }

  /**
   * Checks if the current room overlaps x-coordinates with the given room
   * @param {Room} room The room to check overlap with
   * @return {boolean} True if the rooms overlap x-coordinates
   */
  overlapsX( room ) {
    return !( this.right < room.left || this.left > room.right );
  }

  /**
   * Checks if the current room overlaps y-coordinates with the given room
   * @param {Room} room The room to check overlap with
   * @return {boolean} True if the rooms overlap y-coordinates
   */
  overlapsY( room ) {
    return !( this.bottom < room.top || this.top > room.bottom );
  }

  /**
   * Checks to see if a door is in the corner of a room
   * @param {Door} door The door to a room
   * @return {boolean} True if the door is on the corner of the room
   */
  isCorner( door ) {
    if ( door.x === this.x || door.x === this.x + this.width - 1 ) {
      if ( door.y === this.y || door.y === this.y + this.height - 1 ) {
        return true;
      }
    }
    return false;
  }

  /**
   * Determines if the given coordinates is on the edge of the room
   * @param {number} x The x-coordinate (in tile space)
   * @param {number} y The y-coordinate (in tile space)
   * @return {Edge} The Edge the coordinate lies on
   */
  getEdge( x, y ) {
    if ( y === this.y ) {
      return Edge.TOP;
    }
    else if ( y === this.y + this.height - 1 ) {
      return Edge.BOTTOM;
    }
    else if ( x === this.x ) {
      return Edge.LEFT;
    }
    else if ( x === this.x + this.width - 1 ) {
      return Edge.RIGHT;
    }
    return Edge.NONE;
  }

  /**
   * Spawns a random item
   * @param {Phaser.Scene} scene The current scene
   */
  spawnItem( scene ) {
    ItemDrop.drop(
      scene.map.tileToWorldX( this.centerX + 0.5 ),
      scene.map.tileToWorldY( this.centerY + 0.5 ),
      scene
    );
  }

  /**
   * Spawns the enemies into the room
   * @param {Edge} edge The starting edge of the player
   * @param {Phaser.Scene} scene The current scene
   */
  spawnEnemies( edge, scene ) {
    if ( this.type !== RoomType.BATTLE ) {
      return;
    }

    let numEnemies = 1;
    const size = ( this.width + this.height ) / 2;
    if ( size < 10 ) {
      numEnemies = 2;
    }
    else if ( size >= 10 && size < 13 ) {
      numEnemies = 3;
    }
    else {
      numEnemies = 4;
    }

    const mapping = enemySpawnMap[ edge ][ numEnemies ];
    for ( let i = 0; i < numEnemies; i++ ) {
      const t = mapping.start + i * mapping.inc;

      const x = scene.map.tileToWorldX(
        this.centerX + 0.5 + this.width / 4 * Math.cos( t )
      );
      const y = scene.map.tileToWorldY(
        this.centerY + 0.5 + this.height / 4 * Math.sin( t )
      );
      const enemy = new Enemy( {
        scene: scene,
        key: 'enemy',
        x: x,
        y: y
      } );
      enemy.miniGames.push( 'DanceDance' ); // TODO: Change based on enemy type
      enemy.setHealth( 3 ); // TODO: Change based on enemy type
