/**
 * Generic enemy class for all enemies to extend
 */
export default class Enemy extends Entity {
  /**
   * @constructor
   * @param {SpriteConfig} config The configuration object for the enemy
   */
  constructor( config ) {
    super( config );

    /**
     * List of mini games (the scene key) associated with the entity.
     * @type {Array.<string>}
     */
    this.miniGames = [];
    
    /**
     * The health bar to display above the enemy
     * @type {HealthBar}
     */
    this.healthBar = new HealthBar( config.scene, {
      x: config.x - 25,
      y: config.y - this.height / 2 - 10,
      width: 50,
      height: 5
    } );
    
    /**
     * Whether or not the enemy is selected
     * @type {boolean}
     */
    this.selected = false;
    
    /**
     * The outline to display around the enemy when the enemy is selected
     * @type {Phaser.GameObjects.Rectangle}
     */
    this.outline = null;
    
    /**
     * Whether or not the enemy is active to be updated
     * @type {boolean}
     */
    this.active = true;

    this.setInteractive( { useHandCursor: true } );
  }

  /**
   * Selects the enemy
   */
  select() {
    if ( this.selected ) {
      return;
    }

    this.outline = this.scene.add
      .rectangle( this.x, this.y, this.width, this.height );
    this.outline.setStrokeStyle( 1, 0xFFFF00, 0.75 );
    this.outline.setOrigin( 0.5, 0.5 );
    this.outline.update();
    this.selected = true;
  }

  /**
   * Deselect the enemy
   */
  deselect() {
    if ( !this.selected ) {
      return;
    }
    this.outline.destroy();
    this.selected = false;
  }

  /**
   * @override
   */
  injure( amount ) {
    super.injure( amount );
    this.healthBar.setPercent( this.health / this.maxHealth );
  }

  /**
   * Attacks the given entity and injures them by the given amount
   * @param {Entity} entity The entity to attack
   * @param {number} amount How much damage to do to the entity
   */
  attack( entity, amount ) {
    entity.injure( amount );
  }

  /**
   * @override
   */
  slay() {
    this.active = false;

    // Wait for health bar to finish
    setTimeout( () => {
      BattleDrop.drop( this.x, this.y, this.scene );
      if ( this.healthBar ) {
        this.healthBar.destroy();
      }
      if ( this.outline ) {
        this.outline.destroy();
      }
      this.scene.enemyGroup.remove( this );
      super.slay();
    }, this.healthBar.config.animationDuration );
  }

  /**
   * Gets the key of a mini game from the list of mini games associated with the
   * enemy
   * @return {string|null} A mini game scene key
   */
  getRandomMiniGame() {
    if ( this.miniGames.length === 0 ) {
      return null;
    }
    return this.miniGames[
      Math.floor( Math.random() * this.miniGames.length )
    ];
  }
}

/**
 * Constant used to denote invincibility
 * @type {number}
 */
const INVINCIBLE = -1;

/**
 * Generic entity class for anything that can be interacted with in the game.
 */
export default class Entity extends Phaser.GameObjects.Sprite {
  /**
   * @constructor
   * @param {SpriteConfig} config The configuration object for the entity
   */
  constructor( config ) {
    const { scene, x, y, key } = config;
    super( scene, x, y, key );
    scene.add.existing( this );
    
    /**
     * The current scene
     * @type {Phaser.Scene}
     */
    this.scene = scene;

    /**
     * The maximum amount the health of the entity can be set to.
     * @type {number}
     */
    this.maxHealth = INVINCIBLE;

    /**
     * The current amount of health the entity has
     * @type {number}
     */
    this.health = INVINCIBLE;

    /**
     * Whether or not the entity is alive
     * @type {Boolean}
     */
    this.alive = true;
  }

  /**
   * Sets the current health and max health of the entity
   * @param {number} amount The amount of health the entity has
   */
  setHealth( amount ) {
    this.maxHealth = amount;
    this.health = amount;
  }

  /**
   * Heals the current entity by the given amount
   * @param {number} amount The amount the heal the entity by
   */
  heal( amount ) {
    if ( !this.canHarm() || isNaN( amount ) ) {
      return;
    }

    // take either the max health, or the added amount
    this.health = Math.min( this.health + amount, this.maxHealth );

    // Text Display
    const healTxt = this.scene.add.text( this.x - this.width / 2,
      this.y - this.height, `+${amount}`, {
        fontSize: '16px',
        fontFamily: 'Rye',
        color: '#1ace32',
        fontWeight: 'bold'
      } );
    healTxt.setShadow( 0, 2, '#000', 10 );
    healTxt.setScale( 1 / this.scene.cameras.main.zoom );
    this.scene.tweens.add( {
      targets: healTxt,
      duration: 1000,
      alpha: 0,
      y: this.y - this.height - 20,
      ease: 'Linear'
    } );
    setTimeout( () => healTxt.destroy(), 1000 );
  }

  /**
   * Injures the current entity by the given amount
   * @param {number} amount The amount to injure the entity by
   */
  injure( amount ) {
    if ( !this.canHarm() || isNaN( amount ) ) {
      return;
    }

    // Text Display
    const hurtTxt = this.scene.add.text( this.x - this.width / 2,
      this.y - this.height,
      `-${amount}`, {
        fontSize: '16px',
        fontFamily: 'Rye',
        color: '#e00202',
        fontWeight: 'bold'
      } );
    hurtTxt.setShadow( 0, 2, '#000', 10 );
    hurtTxt.setScale( 1 / this.scene.cameras.main.zoom );
    this.scene.tweens.add( {
      targets: hurtTxt,
      duration: 1000,
      alpha: 0,
      y: this.y - this.height - 20,
      ease: 'Linear'
    } );
    setTimeout( () => hurtTxt.destroy(), 1000 );

    // take either the min health (0), or the subtracted amount
    this.health = Math.max( this.health - amount, 0 );
    if ( this.health === 0 ) {
      this.slay();
    }
  }

  /**
   * If the entity is invincible, then the entity cannot be harmed.
   * @return {boolean} True if the entity can be harmed
   */
  canHarm() {
    return this.maxHealth !== INVINCIBLE;
  }

  /**
   * Called when the player wants to interact with the current entity.
   */
  interactWith() {
    // Does nothing (entity doesn't interact with player)
  }

  /**
   * A nicer way to kill off an entity to remove it from the scene.
   */
  slay() {
    this.alive = false;
    this.destroy();
  }
}

