/**
 * This is a health booster that can only be used when the player has health
 * below their maximum health. If the player's health is already full, the
 * player is injured (what a twisted society we live in)
 */
export default class Injection extends Item {
  /**
   * @constructor
   * @param {number} x The x-position of the item
   * @param {number} y The y-position of the item
   * @param {Phaser.Scene} scene The current scene
   */
  constructor( x, y, scene ) {
    super( { x: x, y: y, scene: scene, key: 'injection' } );
    
    /**
     * The amount to heal the player upon use
     * @type {number}
     */
    this.amount = 5;

    /**
     * This item can be used anywhere
     * @type {ItemType}
     */
    this.itemType = ItemType.ANY;

    /**
     * The name of the item
     * @type {string}
     */
    this.name = 'Injection';
  }

  /**
   * Uses the item to either heal or hurt the player
   * @param {ItemUseConfig} config The configuration passed in
   */
  use( config ) {
    const player = config.player;
    if ( player.health === player.maxHealth ) {
      player.injure( this.amount );
      player.scene.cameras.main.shake( 200, 0.0025 );
      player.scene.cameras.main.flash( 200, 150, 0, 0 );
    }
    else {
      player.heal( this.amount );
    }
  }
}

import { ItemType } from './';

/**
 * The player's inventory which holds the items the player picks up
 */
export default class Inventory {
  /**
   * @constructor
   */
  constructor() {
    /**
     * The items picked up by the player in the game (excludes passive items)
     * @type {Array.<Item>}
     */
    this.items = [];
    
    /**
     * The passive items picked up by the player in the game
     * @type {Array.<Item>}
     */
    this.passive = [];
    
    /**
     * The capacity of the inventory (for non-passive items)
     * @type {number}
     */
    this.capacity = 8;
  }

  /**
   * Adds an item to the inventory
   * @param {Item} item The item to add
   * @return {boolean} True if the item could be added
   */
  addItem( item ) {
    // Passive Items
    if ( item.itemType === ItemType.PASSIVE ) {
      this.passive.push( item.constructor.name );
      return true;
    }
    
    // Non-Passive Items
    if ( this.items.length >= this.capacity ) {
      return false;
    }
    this.items.push( item.constructor.name );
    return true;
  }
}

import Entity from '../Entity';
import { ItemType } from './';

/**
 * This is the base class for all items
 */
export default class Item extends Entity {
  /**
   * @constructor
   * @param {SpriteConfig} config The configuration object for the entity
   */
  constructor( config ) {
    super( config );
    
    /**
     * The type of item
     * @type {ItemType}
     */
    this.itemType = ItemType.ANY;

    // Collision
    this.scene.physics.add.existing( this );
    this.body.setImmovable( true );
    
    /**
     * The collider used to detect collisions with the player
     * @type {Phaser.Physics.Arcade.Collider}
     */
    this.playerCollider = this.scene.physics
      .add.collider( this, this.scene.player, this.onCollide );

    // Interactive
    this.setInteractive( { useHandCursor: true } );
    this.on( 'pointerover', () => {
      this.select();
    } );
    this.on( 'pointerout', () => {
      this.unselect();
    } );

    // Lighting
    this.setPipeline( 'LightPipeline' );
  }

  /**
   * Called when the item is selected
   */
  select() {
    this.setScale( this.scaleX + 0.2 );

    /**
     * The help text displayed to the player when an item is hovered over by the
     * mouse
     * @type {Phaser.GameObjects.Text}
     */
    this.helpText = this.scene.add.text( this.x, this.y, this.name, {
      fontSize: '14px',
      fontFamily: 'Rye',
      color: '#FFFFFF'
    } );
    this.helpText.setShadow( 0, 2, '#333', 10 );
    this.helpText.setScale( 1 / this.scene.cameras.main.zoom );
  }

  /**
   * Called when the item is unselected
   */
  unselect() {
    this.setScale( this.scaleX - 0.2 );
    this.helpText.destroy();
  }

  /**
   * Called when the player collides with the injection.
   * @param {Injection} item The injection item
   * @param {Player} player The player class
   */
  onCollide( item, player ) {
    if ( player.inventory.addItem( item ) ) {
      item.playerCollider.destroy();
      item.destroy();
      if ( item.helpText ) {
        item.helpText.destroy();
      }
    }
  }
}

