import { Difficulty, MiniGame } from './';

/**
 * The Dance Dance mini game is like DDR and the player needs to complete the
 * arrow combination in order before time expires. If a mistake is made or time
 * runs out, the player loses.
 */
export default class DanceDance extends MiniGame {
  /**
   * Creates a DanceDance mini game object
   * @constructor
   */
  constructor() {
    super( 'DanceDance' );
    
    /**
     * The name of the minigame
     * @type {string}
     */
    this.name = 'Dance Dance';
    
    /**
     * The description of the minigame
     * @type {string}
     */
    this.description = 'The player has to do the arrow combination displayed ' +
      'before the time runs out';
    
    /**
     * The text displayed quickly to the user before the minigame begins
     * @type {string}
     */
    this.text = 'Dance!';
    
    /**
     * The time duration of the mini game in ms
     * @type {number}
     */
    this.duration = 3000;
    
    /**
     * The length of the combo required to successfully complete the minigame
     * @type {number}
     */
    this.comboLength = -1;
    
    /**
     * The key combination required to complete the minigame
     * @type {Phaser.Input.Keyboard.KeyCombo}
     */
    this.combo = null;
    
    /**
     * The text displayed to the user to show the combination required
     * @type {Phaser.GameObjects.Text}
     */
    this.danceText = null;
    
    /**
     * The number of times the key was pressed during the minigame
     * @type {number}
     */
    this.inputCount = 0;
  }

  /**
   * @override
   */
  create( data ) {
    super.create( data );

    // Set combo length
    switch ( this.difficulty ) {
      case Difficulty.INTERMEDIATE:
        this.comboLength = 5;
        break;
      case Difficulty.ADVANCED:
        this.comboLength = 6;
        break;
      case Difficulty.EASY:
      default:
        this.comboLength = 4;
        break;
    }

    // Create Key Combination
    const dirs = [ 'W', 'A', 'S', 'D' ];
    const keyCombo = [];
    while ( keyCombo.length < this.comboLength ) {
      keyCombo.push( dirs[ Math.floor( Math.random() * 4 ) ] );
    }
    this.combo = this.input.keyboard.createCombo(
      keyCombo, { resetOnWrongKey: true }
    );
    
    // On combo match, win mini game
    this.input.keyboard.on( 'keycombomatch', () => {
      this.win();
    } );

    // Each time player hits a key, inputCount will be increased
    this.input.keyboard.on( 'keydown', () => {
      this.inputCount++;
    } );

    // Create the display text for the combination
    if ( this.difficulty === Difficulty.ADVANCED ) {
      keyCombo.reverse();
    }
    const txt = keyCombo.map( ( l ) => {
      switch ( l ) {
        case 'W':
          return '';
        case 'A':
          return '';
        case 'S':
          return '';
        case 'D':
          return '';
        default:
          return '';
      }
    } ).join( ' ' );
    this.danceText = this.add.text(
      this.w / 2,
      this.h / 2,
      txt, {
        fontSize: '40px',
        fill: '#FFF'
      }
    );
    this.danceText.setPosition(
      this.danceText.x - this.danceText.width / 2,
      this.danceText.y - this.danceText.height / 2
    );
    this.danceText.setVisible( false );
  }

  /**
   * @see MiniGame#startGame
   */
  startGame() {
    super.startGame();
    this.inputCount = 0;
    this.danceText.setVisible( true );
  }

  /**
   * @override
   */
  updateMiniGame( time, delta ) {
    if ( this.inputCount !== this.combo.index ) {
      this.lose();
    }
  }

  /**
   * @override
   */
  reset() {
    this.combo.destroy();
    super.reset();
  }
}

import MiniGame from './MiniGame';
import DanceDance from './DanceDance';
import { Difficulty } from '../globals';

export {
  MiniGame,
  Difficulty,
  DanceDance
};

import { ItemType } from '../entity/items';
import KeyBinding from '../util/KeyBinding';
import { Difficulty } from '../globals';

/**
 * Base class for all mini games
 */
export default class MiniGame extends Phaser.Scene {
  /**
   * Creates a mini game within the current scene
   * @constructor
   * @param {string} key - The scene key
   */
  constructor( key ) {
    super( { key: key } );
    
    /**
     * The scene key
     * @type {string}
     */
    this.key = key;
    
    /**
     * The name of the minigame
     * @type {string}
     */
    this.name = 'Default MiniGame';
    
    /**
     * The description of the minigame
     * @type {string}
     */
    this.description = 'Default description';

    /**
     * The text displayed quickly to the user before the minigame begins
     * @type {string}
     */
    this.text = 'Default!';

    /**
     * The time duration of the mini game in ms
     * @type {number}
     */
    this.duration = -1;
    
    /**
     * The amount of time elapsed in ms since the minigame started
     * @type {number}
     */
    this.elapsedTime = 0;
    
    /**
     * Whether or not the minigame has started
     * @type {boolean}
     */
    this.started = false;
    
    /**
     * The horizontal position of the minigame window
     * @type {number}
     */
    this.x = 25;
    
    /**
     * The vertical position of the minigame window
     * @type {number}
     */
    this.y = 25;
    
    /**
     * The difficulty of the minigame
     * @type {Difficulty}
     */
    this.difficulty = Difficulty.EASY;
    
    /**
     * The time scale applied to the duration of the minigame
     * @type {number}
     */
    this.timeScale = 1;
    
    /**
     * The parent scene that created this minigame
     * @type {Phaser.Scene}
     */
    this.parent = null;
    
    /**
     * The width of the game
     * @type {number}
     */
    this.w = -1;
    
    /**
     * The height of the game
     * @type {number}
     */
    this.h = -1;
    
    /**
     * The text graphic displayed to the user quickly before the minigame starts
     * @type {Phaser.GameObjects.Text}
     */
    this.alertText = null;
    
    /**
     * The ID of the timeout used for starting the minigame
     * @type {number}
     */
    this.startTimeout = -1;
    
    /**
     * The graphics bar used to display the amount of time left in the minigame
     * @type {Phaser.GameObjects.Graphics}
     */
    this.timerBar = null;
  }

  /**
   * Initializes data when the mini game is created
   * @param {object} data - information passed from parent scene
   */
  create( data ) {
    this.parent = data.parent;
    this.difficulty = data.difficulty;
    this.w = this.sys.game.config.width;
    this.h = this.sys.game.config.height;

    const background = this.add.graphics();
    background.fillStyle( 0x999999, 1 );
    background.fillRect( this.x, this.y, this.w - 50, this.h - 50 );

    this.add.text( this.x + 16, this.y + 16, this.name,
      {
        fontSize: '24px',
        fill: '#FFF'
      } );
    this.add.text( this.x + 16, this.y + 40, this.description,
      {
        fontSize: '12px',
        fill: '#FFF'
      } );

    this.alertText = this.add.text( this.w / 2, this.h / 2, this.text,
      {
        fontSize: '40px',
        fill: '#FFF'
      } );
    this.alertText.setPosition(
      this.alertText.x - this.alertText.width / 2,
      this.alertText.y - this.alertText.height / 2
    );

    // Show text for 1 second, then start mini game
    this.startTimeout = setTimeout( () => {
      this.startGame();
    }, 1000 );

    this.timerBar = this.add.graphics();
    this.timerBar.fillStyle( 0xFFFFFF, 0.4 );
    this.timerBar.fillRect( this.x, this.h - 10 - this.y, this.w - 50, 10 );

    /**
     * Object used to store the keys available for use in the minigame
     * @type {Object}
     */
    this.keys = KeyBinding.createKeys( this, [ 'interact' ] );
  }

  /**
   * Updates every tick of the game loop
   * @param {number} time The current time
   * @param {number} delta The delta time in ms since the last frame
   */
  update( time, delta ) {
    if ( this.started ) {
      if ( this.duration !== -1 ) {
        if ( this.elapsedTime > this.duration ) {
          this.lose();
        }
        this.elapsedTime += delta * this.timeScale;

        this.timerBar.clear();
        this.timerBar.fillStyle( 0xFFFFFF, 0.4 );
        this.timerBar.fillRect( this.x,
          this.h - 10 - this.y,
          this.w - ( ( this.w - 50 ) * ( this.elapsedTime / this.duration ) )
          - 50,
          10
        );
      }
      this.updateMiniGame( time, delta );
    }
    else if ( this.keys.interact.isDown ) {
      this.openInventory();
    }
  }

  /**
   * Opens the inventory
   */
  openInventory() {
    clearTimeout( this.startTimeout );
    this.input.keyboard.resetKeys();
    this.scene
      .launch( 'InventoryScene', {
        parent: this,
        inventory: this.parent.scene.player.inventory,
        context: ItemType.MINIGAME
      } )
      .bringToTop( 'InventoryScene' )
      .pause();
  }

  /**
   * Closes the inventory
   * @param {Item} item The item selected from the inventory
   */
  closeInventory( item ) {
    this.scene.stop( 'InventoryScene' );
    this.input.keyboard.resetKeys();
    this.scene.resume();

    if ( item && item.itemType === ItemType.MINIGAME ) {
      item.use( { minigame: this } );
      this.parent.scene.player.inventory.items.splice( item.inventoryIndex, 1 );
      this.startGame();
    }
    else {
      this.startTimeout = setTimeout( () => {
        this.startGame();
      }, 1000 );
    }
  }

  /**
   * Called right before the mini game starts
   */
  startGame() {
    this.alertText.destroy();
    this.started = true;
  }

  /**
   * This method will only be updated once the mini game has officially started.
   * The mini game will start after the alert text disappears.
   * @param {number} time The current time
   * @param {number} delta The delta time in ms since the last frame
   */
  updateMiniGame( time, delta ) {
    // NOTE: Override this in mini game classes
    throw new Error( 'updateMiniGame() must be overriden' );
  }

  /**
   * Called when the mini game has been won. Will redirect back to combat.
   */
  win() {
    const timeLeft = this.duration - this.elapsedTime;

    this.reset();
    this.parent.continueCombat( {
      win: true,
      damage: Math.ceil( timeLeft / 1000 ) * this.difficulty
    } );
  }

  /**
   * Called when the mini game has been lost. Will redirect back to combat.
   */
  lose() {
    this.reset();
    this.parent.continueCombat( {
      win: false,
      damage: 0
    } );
  }

  /**
   * Resets the mini game to its original state to be played again.
   */
  reset() {
    this.timerBar.destroy();
    this.elapsedTime = 0;
    this.started = false;
    this.timeScale = 1;
  }
}

import { createAnimations } from '../util/Animation';
import { DanceDance } from '../minigames';
import LightPipeline from '../util/LightPipeline';

/**
 * The boot scene, or splash screen, is used to initialize most of the game
 * objects and images. The preload method will be mostly utilized. Once all
 * assets are loaded, the title scene is started.
 */
export default class BootScene extends Phaser.Scene {
  /**
   * Initializes the boot scene
   */
  constructor() {
    super( { key: 'BootScene' } );
  }

  /**
   * @override
   */
  preload() {
    const progress = this.add.graphics();

    // Register a load progress event to show a load bar
    this.load.on( 'progress', ( value ) => {
      progress.clear();
      progress.fillStyle( 0xFFFFFF, 1 );
      progress.fillRect( 0, this.sys.game.config.height
        / 2, this.sys.game.config.width * value, 60 );
    } );

    // Register a load complete event to launch the title screen when all
    // files are loaded
    this.load.on( 'complete', () => {
      createAnimations( this );
      progress.destroy();
      this.scene.start( 'TitleScene' );
    } );

    // Load all assets here
    this.load.script( 'webfont',
      'https://ajax.googleapis.com/ajax/libs/webfont/1.6.26/webfont.js' );
    this.load.image( {
      key: 'dungeon_tiles',
      url: 'assets/dungeon-tileset-ext.png',
      normalMap: 'assets/dungeon-tileset-ext_n.png'
    } );

    this.loadItems();
    this.loadSprites();

    // TODO: DELETE THIS SAMPLE
    this.load.atlas( 'sample-sprites', 'assets/sample-sprites.png',
      'assets/sample-sprites.json' );

    this.scene.add( 'DanceDance', DanceDance );
    this.game.renderer.addPipeline( 'LightPipeline', new LightPipeline( {
      game: this.game,
      renderer: this.game.renderer,
      maxLights: 10
    } ) );
  }

  /**
   * Loads the sprites
   */
  loadSprites() {
    this.load.image( 'enemy', 'assets/enemy.png' );
    this.load.image( {
      key: 'player',
      url: 'assets/player/player.png',
      normalMap: 'assets/player/player_n.png'
    } );
  }

  /**
   * Loads the items
   */
  loadItems() {
    this.loadItem( 'injection' );
    this.loadItem( 'timelapse' );
    this.loadItem( 'vorpal_sword' );
    this.loadItem( 'bug_spray' );
  }

  /**
   * Loads the image for an item based on its name
   * @param {string} name The name of the item
   */
  loadItem( name ) {
    this.load.image( {
      key: name,
      url: `assets/items/${name}/${name}.png`,
      normalMap: `assets/items/${name}/${name}_n.png`
    } );
  }
}

/**
 * A display message that appears
 */
export default class Message {
  /**
   * Creates a status message at the bottom of the screen
   * @param {string} msg The message to display
   * @param {Phaser.Scene} scene The scene to show the message on
   */
  static status( msg, scene ) {
    if ( scene.statusMsg ) {
      clearTimeout( scene.statusMsg.fadeTimeout );
      clearTimeout( scene.statusMsg.removeTimeout );
      scene.statusMsg.destroy();
    }

    scene.statusMsg = scene.add.text( -scene.game.config.width / 2,
      scene.game.config.height - 30, msg, {
        fontSize: '16px',
        fontFamily: 'Rye',
        color: '#FFF'
      } );
    scene.statusMsg.setPosition( scene.statusMsg.x - scene.statusMsg.width / 2,
      scene.statusMsg.y );
    scene.statusMsg.setShadow( 0, 2, '#000', 10 );
    scene.statusMsg.fadeTimeout = setTimeout( () => {
      scene.tweens.add( {
        targets: scene.statusMsg,
        duration: 1000,
        alpha: 0,
        ease: 'Linear'
      } );
      scene.statusMsg.removeTimeout = setTimeout( () => {
        scene.statusMsg.destroy();
        scene.statusMsg = null;
      }, 1000 );
    }, 1500 );
  }
}

import KeyBinding from '../util/KeyBinding';
import Dungeon from '../dungeon/Dungeon';
import Hallway from '../dungeon/Hallway';
import { Edge, Orientation, RoomType } from '../globals';
import Tileset from '../dungeon/TileMappings';
import TilemapVisibility from '../dungeon/TilemapVisibility';
import { Player } from '../entity';
import Battle from '../battle/Battle';
import BattleDrop from '../battle/BattleDrop';
import { ItemType } from '../entity/items';

/**
 * The game scene is the main scene used when the player is in actual game play.
 * All mini game and dungeon gameplay will be from this scene.
 */
export default class GameScene extends Phaser.Scene {
  /**
   * Initializes the game scene
   * @constructor
   */
  constructor() {
    super( { key: 'GameScene' } );
  }

  /**
   * @override
   */
  preload() {
  }

  /**
   * @override
   */
  create() {
    /**
     * A collection of the timeouts currently pending based on the number of
     * rooms visited since the timeout was added
     * @type {Map.<string, {remaining: number, callback: function}>}
     */
    this.roomCooloffs = {};
    
    /**
     * A collection of the keys available for use in the game
     * @type {Object}
     */
    this.keys = KeyBinding.createKeys( this,
      [ 'up', 'left', 'right', 'down', 'space', 'interact', 'pause' ] );
    
    /**
     * The scene groups to hold all of the enemies present
     * @type {Phaser.GameObjects.Group}
     */
    this.enemyGroup = this.add.group();
    
    /**
     * The current battle in progress
     * @type {Battle}
     */
    this.battle = null;
    
    /**
     * The dungeon the player is currently in
     * @type {Dungeon}
     */
    this.dungeon = null;
    
    /**
     * The width of the map in pixels
     * @type {number}
     */
    this.mapWidthInPixels = -1;
    
    /**
     * The height of the map in pixels
     * @type {number}
     */
    this.mapHeightInPixels = -1;
    
    /**
     * The current map displayed to the user
     * @type {Phaser.Tilemaps.Tilemap}
     */
    this.map = null;
    
    /**
     * The general layer building the dungeon (walls, floors)
     * @type {Phaser.Tilemaps.DynamicTilemapLayer}
     */
    this.groundLayer = null;
    
    /**
     * The layer that contains all of the objects in the dungeon (i.e. doors)
     * @type {Phaser.Tilemaps.DynamicTilemapLayer}
     */
    this.stuffLayer = null;
    
    /**
     * Used to show / hide the dungeon
     * @type {TilemapVisibility}
     */
    this.tilemapVisibility = null;
    
    /**
     * The player in the dungeon
     * @type {Player}
     */
    this.player = null;
    
    this.createDungeonMap();
    this.createPlayer();
    this.formatCamera();
    this.configCameraHUD();
  }

  /**
   * @override
   */
  update( time, delta ) {
    this.player.update( time, delta );
    if ( this.battle && this.battle.active ) {
      this.battle.update();
    }
    else {
      this.updateMapVisibility();
    }

    if ( this.keys.interact.isDown ) {
      this.openInventory();
    }

    if ( this.keys.pause.isDown ) {
      this.pause();
    }
  }

  /**
   * Pauses the game
   */
  pause() {
    this.input.keyboard.resetKeys();
    this.scene
      .launch( 'PauseScene', { parent: this } )
      .bringToTop( 'PauseScene' )
      .pause();
  }

  /**
   * Unpauses the game
   */
  unpause() {
    this.scene.stop( 'PauseScene' );
    this.input.keyboard.resetKeys();
    this.scene.resume();
  }

  /**
   * Opens the inventory
   */
  openInventory() {
    this.input.keyboard.resetKeys();
    this.scene
      .launch( 'InventoryScene', {
        parent: this,
        inventory: this.player.inventory,
        context: ItemType.ANY
      } )
      .bringToTop( 'InventoryScene' )
      .pause();
  }

  /**
   * Closes the inventory
   * @param {Item} item The item selected from the inventory
   */
  closeInventory( item ) {
    this.scene.stop( 'InventoryScene' );
    this.input.keyboard.resetKeys();
    this.scene.resume();

    if ( item && item.itemType === ItemType.ANY ) {
      item.use( {
        player: this.player,
        scene: this,
        battle: this.battle
      } );
      this.player.inventory.items.splice( item.inventoryIndex, 1 );
    }
  }

  /**
   * Creates the HUD camera
   */
  configCameraHUD() {
    const { width, height } = this.game.config;
    const HUD_X = -width;
    const hudCamera = this.cameras.add( 0, 0, width, height, false, 'HUD' );
    hudCamera.scrollX = HUD_X;
  }

  /**
   * Updates the visibility of the map
   */
  updateMapVisibility() {
    const playerTileX = this.groundLayer.worldToTileX( this.player.x );
    const playerTileY = this.groundLayer.worldToTileY( this.player.y );
    const playerRoom = this.dungeon.getRoomAt( playerTileX, playerTileY );

    if ( this.tilemapVisibility.setActiveRoom( playerRoom ) ) {
      if ( !playerRoom.entered ) {
        // Battle Room
        if ( playerRoom.type === RoomType.BATTLE && !this.preventSpawn ) {
          const edge = playerRoom.getEdge( playerTileX, playerTileY );
          if ( Edge.TOP === edge ) {
            this.player.setDestination(
              this.player.x,
              this.player.y + this.map.tileHeight * 2,
              () => {
                this.beginCombat( playerRoom, Edge.TOP );
              }
            );
          }
          else if ( Edge.BOTTOM === edge ) {
            this.player.setDestination(
              this.player.x,
              this.player.y - this.map.tileHeight * 2,
              () => {
                this.beginCombat( playerRoom, Edge.BOTTOM );
              }
            );
          }
          else if ( Edge.LEFT === edge ) {
            this.player.setDestination(
              this.player.x + this.map.tileWidth * 2,
              this.player.y,
              () => {
                this.beginCombat( playerRoom, Edge.LEFT );
              }
            );
          }
          // Right
          else if ( Edge.RIGHT === edge ) {
            this.player.setDestination(
              this.player.x - this.map.tileWidth * 2,
              this.player.y,
              () => {
                this.beginCombat( playerRoom, Edge.RIGHT );
              }
            );
          }
        }
        // Item Room
        else if ( playerRoom.type === RoomType.ITEM ) {
          playerRoom.spawnItem( this );
        }
      }

      // Only reduce room cooloff when entering rooms
      if ( !( playerRoom instanceof Hallway ) ) {
        const shouldDelete = [];
        Object.keys( this.roomCooloffs ).forEach( ( key ) => {
          this.roomCooloffs[ key ].remaining--;
          if ( this.roomCooloffs[ key ].remaining <= 0 ) {
            this.roomCooloffs[ key ].callback();
            shouldDelete.push( key );
          }
        } );
        shouldDelete.forEach( ( key ) => {
          delete this.roomCooloffs[ key ];
        } );
      }
    }

    playerRoom.entered = true;
  }

  /**
   * Begins combat inside of a battle room
   * @param {Room} room The room to begin a battle in
   * @param {number} edge The edge the player entered on
   */
  beginCombat( room, edge ) {
    this.battle = new Battle( room, edge, this );
    this.battle.begin();
  }

  /**
   * Ends the combat inside of the battle room
   */
  endCombat() {
    this.battle.end()
      .then( () => this.battle = null );
  }

  /**
   * Creates the map of the dungeon
   */
  createDungeonMap() {
    this.dungeon = new Dungeon();

    // Initialize Dungeon Map
    const map = this.make.tilemap( {
      tileWidth: 32,
      tileHeight: 32,
      width: this.dungeon.width,
      height: this.dungeon.height
    } );
    this.mapWidthInPixels = map.widthInPixels;
    this.mapHeightInPixels = map.heightInPixels;
    this.map = map;

    // Create Tilesets & Layers
    const tileset = map.addTilesetImage( 'dungeon_tiles', null, 32, 32, 1, 2 );
    this.groundLayer =
      map.createBlankDynamicLayer( 'Ground', tileset ).fill( Tileset.BLANK );
    this.stuffLayer = map.createBlankDynamicLayer( 'Stuff', tileset );
    const shadowLayer = map.createBlankDynamicLayer( 'Shadow', tileset )
      .fill( Tileset.BLANK );

    this.tilemapVisibility = new TilemapVisibility( this, shadowLayer );

    // Create Rooms in Dungeon
    this.dungeon.rooms.forEach( ( room ) => {
      if ( room instanceof Hallway ) {
        this.createHallway( room );
      }
      else {
        this.createRoom( room );
      }
    } );

    // Collision
    this.groundLayer.setCollisionByExclusion( [ -1, 9 ] );
    this.stuffLayer.setCollision( [ 24, 25, 26, 32, 40 ] );
  }

  /**
   * Creates a hallway on the ground layer
   * @param {Hallway} hallway the hallway being added
   */
  createHallway( hallway ) {
    const { width, height, left, right, top, bottom } = hallway;
    if ( hallway.orientation === Orientation.HORIZONTAL ) {
      this.groundLayer.fill( Tileset.WALL.TOP, left, top, width, 1 );
      this.groundLayer.fill( Tileset.FLOOR, left, top + 1, width, 1 );
      this.groundLayer.fill( Tileset.WALL.BOTTOM, left, bottom, width, 1 );
    }
    else if ( hallway.orientation === Orientation.VERTICAL ) {
      this.groundLayer.fill( Tileset.WALL.LEFT, left, top, 1, height );
      this.groundLayer.fill( Tileset.FLOOR, left + 1, top, 1, height );
      this.groundLayer.fill( Tileset.WALL.RIGHT, right, top, 1, height );
    }
  }

  /**
   * Creates a room on the ground layer
   * @param {Room} room the room being added
   */
  createRoom( room ) {
    const { x, y, width, height, left, right, top, bottom } = room;

    // Fill the floor
    this.groundLayer.fill( Tileset.FLOOR, x + 1, y + 1, width - 2, height - 2 );

    // Place the room corners tiles
    this.groundLayer.putTileAt( Tileset.WALL.TOP_LEFT, left, top );
    this.groundLayer.putTileAt( Tileset.WALL.TOP_RIGHT, right, top );
    this.groundLayer.putTileAt( Tileset.WALL.BOTTOM_LEFT, left, bottom );
    this.groundLayer.putTileAt( Tileset.WALL.BOTTOM_RIGHT, right, bottom );

    // Fill the walls
    this.groundLayer.fill( Tileset.WALL.TOP, left + 1, top, width - 2, 1 );
    this.groundLayer.fill( Tileset.WALL.BOTTOM, left + 1,
      bottom, width - 2, 1 );
    this.groundLayer.fill( Tileset.WALL.LEFT, left, top + 1, 1, height - 2 );
    this.groundLayer.fill( Tileset.WALL.RIGHT, right, top + 1,
      1, height - 2 );

    // Doors
    let tl; // Top-Left
    let tr; // Top-Right
    let bl; // Bottom-Left
    let br; // Bottom-Right
    let lt; // Left-Top
    let lb; // Left-Bottom
    let rt; // Right-Top
    let rb; // Right-Bottom
    room.doors.forEach( ( door ) => {
      this.groundLayer.putTileAt( Tileset.FLOOR,
        x + door.x, y + door.y );

      // TOP
      if ( Edge.TOP === door.edge ) {
        if ( door.x === 1 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.TOP.LEFT, x, y );
          tl = true;
        }
        else if ( door.x === room.width - 2 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.TOP.RIGHT,
            x + door.x - 1, y );
          tr = true;
        }
        else {
          this.groundLayer.putTilesAt( Tileset.DOOR.TOP.MIDDLE,
            x + door.x - 1, y );
        }
      }
      // BOTTOM
      else if ( Edge.BOTTOM === door.edge ) {
        if ( door.x === 1 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.BOTTOM.LEFT,
            x, y + height - 1 );
          bl = true;
        }
        else if ( door.x === room.width - 2 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.BOTTOM.RIGHT,
            x + door.x - 1, y + height - 1 );
          br = true;
        }
        else {
          this.groundLayer.putTilesAt( Tileset.DOOR.BOTTOM.MIDDLE,
            x + door.x - 1, y + height - 1 );
        }
      }
      // LEFT
      else if ( Edge.LEFT === door.edge ) {
        if ( door.y === 1 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.LEFT.TOP, x, y );
          lt = true;
        }
        else if ( door.y === room.height - 2 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.LEFT.BOTTOM,
            x, y + door.y - 1 );
          lb = true;
        }
        else {
          this.groundLayer.putTilesAt( Tileset.DOOR.LEFT.MIDDLE,
            x, y + door.y - 1 );
        }
      }
      // RIGHT
      else if ( Edge.RIGHT === door.edge ) {
        if ( door.y === 1 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.RIGHT.TOP,
            x + width - 1, y );
          rt = true;
        }
        else if ( door.y === room.height - 2 ) {
          this.groundLayer.putTilesAt( Tileset.DOOR.RIGHT.BOTTOM,
            x + width - 1, y + door.y - 1 );
          rb = true;
        }
        else {
          this.groundLayer.putTilesAt( Tileset.DOOR.RIGHT.MIDDLE,
            x + width - 1, y + door.y - 1 );
        }
      }
    } );

    // Check for doors at corners
    if ( tl && lt ) { // Top-Left and Left-Top
      this.groundLayer.putTileAt( Tileset.CORNER.BOTTOM_RIGHT, x, y );
    }
    if ( tr && rt ) { // Top-Right and Right-Top
      this.groundLayer.putTileAt( Tileset.CORNER.BOTTOM_LEFT,
        x + width - 1, y );
    }
    if ( bl && lb ) { // Bottom-Left and Left-Bottom
      this.groundLayer.putTileAt( Tileset.CORNER.TOP_RIGHT,
        x, y + height - 1 );
    }
    if ( br && rb ) { // Bottom-Right and Right-Bottom
      this.groundLayer.putTileAt( Tileset.CORNER.TOP_LEFT,
        x + width - 1, y + height - 1 );
    }
  }

  /**
   * Adds a player to the room. Also adds colliders to existing layers.
   */
  createPlayer() {
    const sX = this.map.tileToWorldX( this.dungeon.startRoom.centerX );
    const sY = this.map.tileToWorldY( this.dungeon.startRoom.centerY );
    this.player = new Player( sX, sY, this );
    this.physics.add.existing( this.player );

    this.physics.add.collider( this.player, this.groundLayer );
    
    /**
     * The collider used to detect collisions between the player and the stuff
     * layer
     * @type {Phaser.Physics.Arcade.Collider}
     */
    this.stuffCollider =
      this.physics.add.collider( this.player, this.stuffLayer );

    BattleDrop.drop( sX - 25, sY - 25, this );
  }

  /**
   * Formats the camera to the generated dungeon, starts following the player
   * object, and sets the render pipelines for layers affected by light
   */
  formatCamera() {
    const camera = this.cameras.main;
    camera.setBounds( 0, 0, this.mapWidthInPixels, this.mapHeightInPixels );
    camera.startFollow( this.player );
    camera.setZoom( 1.5 );

    this.lights.enable().setAmbientColor( 0x111111 );
    this.groundLayer.setPipeline( 'LightPipeline' );
    this.stuffLayer.setPipeline( 'LightPipeline' );
  }

  /**
   * Called when the player is dead
   */
  gameOver() {
    setTimeout( () => {
      this.battle = null;
      this.scene.start( 'TitleScene' );
    }, 200 );
  }
}

import KeyBinding from '../util/KeyBinding';
import { itemClass } from '../entity/items';

/**
 * The inventory scene is the scene that displays the inventory to the player.
 */
export default class InventoryScene extends Phaser.Scene {
  /**
   * Initializes the inventory scene
   * @constructor
   */
  constructor() {
    super( { key: 'InventoryScene' } );
    
    /**
     * The horizontal position of the scene
     * @type {number}
     */
    this.x = 25;
    
    /**
     * The vertical position of the scene
     * @type {number}
     */
    this.y = 25;
    
    /**
     * The parent scene used to initiate this scene
     * @type {Phaser.Scene}
     */
    this.parent = null;
    
    /**
     * The inventory to display
     * @type {Inventory}
     */
    this.inventory = null;
    
    /**
     * The item context to help display to the user only the items that can be
     * used at that given time
     * @type {ItemType}
     */
    this.context = null;
  }

  /**
   * Initializes data when the inventory is created
   * @param {object} data - information passed from parent scene
   */
  create( data ) {
    this.parent = data.parent;
    this.inventory = data.inventory;
    this.context = data.context;
    
    /**
     * The width of the game
     * @type {number}
     */
    this.w = this.sys.game.config.width;
    
    /**
     * The height of the game
     * @type {number}
     */
    this.h = this.sys.game.config.height;

    WebFont.load( {
      google: { families: [ 'Rye' ] },
      active: () => {
        const titleText = this.add.text( this.w / 2, 10, 'Inventory', {
          fontSize: '40px',
          color: '#FFF',
          fontFamily: 'Rye'
        } );
        titleText.setShadow( 0, 2, '#333', 10 );
        titleText.setPosition( this.w / 2 - titleText.width / 2,
          titleText.height / 2 + 10 );
      }
    } );

    const background = this.add.graphics();
    background.fillStyle( 0x999999, 1 );
    background.fillRect( this.x, this.y, this.w - 50, this.h - 50 );

    const border = this.add.graphics();
    border.lineStyle( 4, 0x7b654f, 1 );
    border.strokeRect( this.x, this.y, this.w - 50, this.h - 50 );

    /**
     * A collection of keys available for use in the inventory
     * @type {Object}
     */
    this.keys = KeyBinding.createKeys( this,
      [ 'up', 'left', 'right', 'down', 'space', 'interact' ] );

    this.lights.enable().setAmbientColor( 0x111111 );
    this.lights.addLight( this.w / 2, this.h / 2, this.w * 2, 0xFFFFFF, 1 );

    // Normal Items
    this.inventory.items.forEach( ( item, index ) => {
      const Item = itemClass( item );
      const i = new Item( this.x + 40 + index * 64, this.y + 100, this );
      i.inventoryIndex = index;
      i.setScale( 2 );

      if ( i.itemType !== this.context ) {
        i.setAlpha( 0.25 );
      }
      else {
        i.on( 'pointerdown', () => {
          this.parent.closeInventory( i );
        } );
      }
    } );

    // Passive Items
    this.inventory.passive.forEach( ( item, index ) => {
      const Item = itemClass( item );
      const i = new Item( this.x + 40 + index * 64, this.y + 400,
        this );
      i.inventoryIndex = index;
      i.setScale( 2 );
    } );
  }

  /**
   * Updates every tick of the game loop
   * @param {number} time The current time
   * @param {number} delta The delta time in ms since the last frame
   */
  update( time, delta ) {
    if ( this.keys.interact.isDown ) {
      this.parent.closeInventory();
      this.input.keyboard.resetKeys();
    }
  }
}

import KeyBinding from '../util/KeyBinding';

/**
 * The pause scene is the scene displayed when hitting the ESC key in the game
 */
export default class PauseScene extends Phaser.Scene {
  /**
   * @constructor
   */
  constructor() {
    super( { key: 'PauseScene' } );
    
    /**
     * The parent scene used to initiate this scene
     * @type {Phaser.Scene}
     */
    this.parent = null;
  }

  /**
   * Initializes data when the pause scene is created
   * @param {object} data - information passed from parent scene
   */
  create( data ) {
    this.parent = data.parent;
    
    /**
     * A collection of keys available for use in the inventory
     * @type {Object}
     */
    this.keys = KeyBinding.createKeys( this,
      [ 'up', 'left', 'right', 'down', 'space', 'pause' ] );

    const { width, height } = this.game.config;

    // Background
    const background = this.add.graphics();
    background.fillStyle( 0x333333, 0.9 );
    background.fillRect( 0, 0, width, height );

    WebFont.load( {
      google: { families: [ 'Rye' ] },
      active: () => {
        const titleText = this.add.text( width / 2, 10, 'Pause Menu', {
          fontSize: '40px',
          color: '#FFF',
          fontFamily: 'Rye'
        } );
        titleText.setShadow( 0, 2, '#000', 10 );
        titleText.setPosition( width / 2 - titleText.width / 2,
          titleText.height / 2 + 10 );

        const controls = [
          'W - Move Up',
          'A - Move Left',
          'S - Move Down',
          'D - Move Right',
          'E - Inventory',
          'Space - Select',
          'Escape - Pause',
          '',
          'Left Mouse - Select'
        ];
        controls.forEach( ( control, index ) => {
          this.add.text( 40, 100 + 30 * index, control, {
            fontSize: '24px',
            color: '#FFF'
          } ).setShadow( 0, 2, '#000', 10 );
        } );
      }
    } );
  }

  /**
   * Updates every tick of the game loop
   * @param {number} time The current time
   * @param {number} delta The delta time in ms since the last frame
   */
  update( time, delta ) {
    if ( this.keys.pause.isDown ) {
      this.parent.unpause();
      this.input.keyboard.resetKeys();
    }
  }
}

import KeyBinding from '../util/KeyBinding';
import WebFont from 'webfontloader';

/**
 * The title scene displays the main menu for the player to choose what they
 * would like to do
 */
export default class TitleScene extends Phaser.Scene {
  /**
   * Initializes the title scene
   * @constructor
   */
  constructor() {
    super( { key: 'TitleScene' } );
    
    /**
     * The title of the game
     * @type {string}
     */
    this.gameTitle = 'Micrungeon';
    
    /**
     * The current version of the game
     * @type {string}
     */
    this.version = '0.1.0';
    
    /**
     * The menu index of the option currently selected
     * @type {number}
     */
    this.index = 0;
    
    /**
     * A collection of menu options displayed to the user
     * @type {Array.<Phaser.GameObjects.Text>}
     */
    this.options = [];
  }

  /**
   * @override
   */
  preload() {

  }

  /**
   * @override
   */
  create() {
    this.scene.setVisible( false );
    
    /**
     * A collection of keys available for use in the inventory
     * @type {Object}
     */
    this.keys = KeyBinding.createKeys( this, [ 'up', 'down', 'space' ] );
    const { width, height } = this.game.config;

    // Background
    const background = this.add.graphics();
    background.fillStyle( 0x333333, 1 );
    background.fillRect( 0, 0, width, height );

    // Version Text
    const versionText = this.add.text( 10, height - 22,
      `v${this.version}`, {
        fontSize: '12px',
        color: '#FFF'
      } );
    versionText.setPosition( width - versionText.width - 10, height - 22 );

    // Title Text
    WebFont.load( {
      google: { families: [ 'Rye' ] },
      active: () => {
        const titleText = this.add.text( 10, 10, this.gameTitle, {
          fontSize: '64px',
          color: '#FFF',
          fontFamily: 'Rye'
        } );
        titleText.setShadow( 0, 2, '#000', 10 );
        this.scene.setVisible( true );
      }
    } );

    // Menu Options
    this.options = [
      this.add.text( 20, 0, 'Play', {
        fontSize: '30px',
        color: '#FFF'
      } ),
      this.add.text( 20, 0, 'Options', {
        fontSize: '30px',
        color: '#FFF'
      } ),
      this.add.text( 20, 0, 'Credits', {
        fontSize: '30px',
        color: '#FFF'
      } )
    ];

    for ( let i = 0; i < this.options.length; i++ ) {
      const opt = this.options[ i ];
      opt.setShadow( 0, 2, '#000', 5 );
      opt.setInteractive( { useHandCursor: true } );
      opt.setPosition( 20, 120 + ( opt.height + 20 ) * i );
      opt.on( 'pointerover', () => {
        this.index = i;
      } );
      opt.on( 'pointerdown', () => {
        this.selectOption();
      } );
    }
  }

  /**
   * @override
   */
  update( time, delta ) {
    // Key UP
    if ( this.keys.up.isDown ) {
      this.index--;
      if ( this.index < 0 ) {
        this.index = this.options.length - 1;
      }
      this.input.keyboard.resetKeys();
    }
    else if ( this.keys.down.isDown ) {
      this.index = ( this.index + 1 ) % this.options.length;
      this.input.keyboard.resetKeys();
    }
    else if ( this.keys.space.isDown ) {
      this.selectOption();
      this.input.keyboard.resetKeys();
    }

    // Change option alpha based on selected index
    for ( let i = 0; i < this.options.length; i++ ) {
      if ( i === this.index ) {
        this.options[ i ].setAlpha( 1 );
      }
      else {
        this.options[ i ].setAlpha( 0.5 );
      }
    }
  }

  /**
   * Performs an action based on the current selected index
   */
  selectOption() {
    switch ( this.index ) {
      case 0:
        this.scene.start( 'GameScene' );
        break;
    }
  }
}

/**
 * Creates all of the animations for entities
 * @param {Phaser.Scene} scene The scene to add the animation to
 */
function createAnimations( scene ) {
  // TODO: DELETE THIS SAMPLE
  addDynamicAnim( scene, 'sample', 'sample/frame', 'sample-sprites', 1, 3, 5 );
}

/**
 * Adds a static animation to the given scene. A static animation is a one
 * frame animation that doesn't change.
 * @param {Phaser.Scene} scene The scene to add the animation to
 * @param {string} animKey  The key that the animation will be associated with
 * @param {string} frameKey The key that describes the filename in the JSON
 * @param {string} atlasKey The key that describes the loaded Atlas
 */
function addStaticAnim( scene, animKey, frameKey, atlasKey ) {
  scene.anims.create( {
    key: animKey,
    frames: [ {
      frame: frameKey,
      key: atlasKey
    } ]
  } );
}

/**
 * Adds a dynamic animation to the given scene. A dynamic animation is a
 * tradition animation where it looks like the entity is moving.
 * @param {Phaser.Scene} scene The scene to add the animation to
 * @param {string} animKey The key that the animation will be associated with
 * @param {string} framePrefix The prefix of the key that describes the file
 * @param {string} atlasKey The key that describes the loaded Atlas
 * @param {Number} start The number of the first frame
 * @param {Number} end The number of the last frame
 * @param {Number} [frameRate=10] The frame rate of playback in fps
 * @param {Number} [repeat=-1] Number of times to repeat the animation (-1
 *  for infinity)
 * @param {Number} [repeatDelay=0] Delay before the animation repeats. Value
 *  given in milliseconds.
 */
function addDynamicAnim( scene, animKey, framePrefix, atlasKey, start, end,
  frameRate = 10, repeat = -1, repeatDelay = 0 ) {
  scene.anims.create( {
    key: animKey,
    frames: scene.anims.generateFrameNames( atlasKey, {
      prefix: framePrefix,
      start: start,
      end: end
    } ),
    frameRate: frameRate,
    repeat: repeat,
    repeatDelay: repeatDelay
  } );
}

export {
  addStaticAnim,
  addDynamicAnim,
  createAnimations
};

/**
 * Collection of common key bindings to use in the game
 * @type {Map.<string, Phaser.Input.Keyboard.KeyCode>}
 */
const BINDINGS = {
  LEFT: Phaser.Input.Keyboard.KeyCodes.A,
  RIGHT: Phaser.Input.Keyboard.KeyCodes.D,
  UP: Phaser.Input.Keyboard.KeyCodes.W,
  DOWN: Phaser.Input.Keyboard.KeyCodes.S,
  INTERACT: Phaser.Input.Keyboard.KeyCodes.E,
  SPACE: Phaser.Input.Keyboard.KeyCodes.SPACE,
  PAUSE: Phaser.Input.Keyboard.KeyCodes.ESC
};

/**
 * The KeyBinding utility class provides a convenient way to create key bindings
 * for a scene.
 */
export default class KeyBinding {
  /**
   * Creates a keys object based on the given bindings. This also adds the given
   * bindings to the given scene.
   * @param {Phaser.Scene} scene The current scene
   * @param {array} bindings An array of key bindings
   * @return {object} the constructed keys object
   */
  static createKeys( scene, bindings ) {
    const keys = {};
    bindings.forEach( ( binding ) => {
      keys[ binding ] = scene.input.keyboard.addKey(
        BINDINGS[ binding.toUpperCase() ] );
    } );
    return keys;
  }
}

/**
 * Constant used to simplify calling the
 * Phaser.Renderer.WebGL.Pipelines.TextureTintPipeline class
 * @type {Phaser.Renderer.WebGL.Pipelines.TextureTintPipeline}
 */
const TextureTintPipeline = Phaser.Renderer.WebGL.Pipelines.TextureTintPipeline;

/**
 * The maximum number of lights allowed to render in a scene
 * @type {number}
 */
let LIGHT_COUNT = 10;

/**
 * Custom light pipeline to fix issues with the current diffuse lighting
 * pipeline.
 */
export default class LightPipeline
  extends Phaser.Renderer.WebGL.Pipelines.ForwardDiffuseLightPipeline {
  /**
   * @constructor
   * @override
   */
  constructor( config ) {
    super( config );

    LIGHT_COUNT = config.maxLights;

    /* eslint-disable */
    config.fragShader = [
      '#define SHADER_NAME PHASER_FORWARD_DIFFUSE_FS',
      '',
      'precision mediump float;',
      '',
      'struct Light',
      '{',
      '    vec2 position;',
      '    vec3 color;',
      '    float intensity;',
      '    float radius;',
      '    float hWidth;',
      '    float hHeight;',
      '};',
      '',
      'const int kMaxLights = %LIGHT_COUNT%;',
      '',
      'uniform vec4 uCamera; /* x, y, rotation, zoom */',
      'uniform vec2 uResolution;',
      'uniform sampler2D uMainSampler;',
      'uniform sampler2D uNormSampler;',
      'uniform vec3 uAmbientLightColor;',
      'uniform Light uLights[kMaxLights];',
      '',
      'varying vec2 outTexCoord;',
      'varying vec4 outTint;',
      '',
      'void main()',
      '{',
      '    vec3 finalColor = vec3(0.0, 0.0, 0.0);',
      '    vec4 color = texture2D(uMainSampler, outTexCoord) * vec4(outTint.rgb * outTint.a, outTint.a);',
      '    vec3 normalMap = texture2D(uNormSampler, outTexCoord).rgb;',
      '    vec3 normal = normalize(vec3(normalMap * 2.0 - 1.0));',
      '    vec2 res = vec2(min(uResolution.x, uResolution.y)) * uCamera.w;',
      '',
      '    for (int index = 0; index < kMaxLights; ++index)',
      '    {',
      '        Light light = uLights[index];',
      '        vec3 lightDir = vec3((light.position.xy / res) - (gl_FragCoord.xy / res), 0.2);',
      '        vec3 lightNormal = normalize(lightDir);',
      '        float distToSurf = length(lightDir) * uCamera.w;',
      '        float diffuseFactor = max(dot(normal, lightNormal), 0.0);',
      '        float radius = (light.radius / res.x * uCamera.w) * uCamera.w;',
      '        float attenuation = clamp(1.0 - distToSurf * distToSurf / (radius * radius), 0.0, 1.0);',
      '        vec3 diffuse = light.color * diffuseFactor;',
      '        finalColor += (attenuation * diffuse) * light.intensity;',
      '    }',
      '',
      '    vec4 colorOutput = vec4(uAmbientLightColor + finalColor, 1.0);',
      '    gl_FragColor = color * vec4(colorOutput.rgb * colorOutput.a, colorOutput.a);',
      '',
      '}',
      ''
    ].join( '\n' ).replace( '%LIGHT_COUNT%', LIGHT_COUNT.toString() );
    /* eslint-enable */

    TextureTintPipeline.call( this, config );
  }

  /**
   * @override
   */
  onBind( gameObject ) {
    TextureTintPipeline.prototype.onBind.call( this );

    const renderer = this.renderer;
    const program = this.program;

    this.mvpUpdate();

    renderer.setInt1( program, 'uNormSampler', 1 );
    renderer.setFloat2( program, 'uResolution', this.width, this.height );

    if ( gameObject ) {
      this.setNormalMap( gameObject );
    }

    return this;
  }

  /**
   * @override
   */
  onRender( scene, camera ) {
    /**
     * Whether or not the pipeline is active
     * @type {boolean}
     */
    this.active = false;

    const lightManager = scene.sys.lights;

    if ( !lightManager || lightManager.lights.length <= 0 ||
      !lightManager.active ) {
      // Passthru
      return this;
    }

    const lights = lightManager.cull( camera );
    const lightCount = Math.min( lights.length, LIGHT_COUNT );

    if ( lightCount === 0 ) {
      return this;
    }

    this.active = true;

    const renderer = this.renderer;
    const program = this.program;
    const cameraMatrix = camera.matrix;
    const point = {
      x: 0,
      y: 0
    };
    const height = renderer.height;
    let index;

    for ( index = 0; index < LIGHT_COUNT; ++index ) {
      // Reset lights
      renderer.setFloat1( program, 'uLights[' + index + '].radius', 0 );
    }

    renderer.setFloat4( program, 'uCamera', camera.x, camera.y,
      camera.rotation, camera.zoom );
    renderer.setFloat3( program, 'uAmbientLightColor',
      lightManager.ambientColor.r, lightManager.ambientColor.g,
      lightManager.ambientColor.b );

    for ( index = 0; index < lightCount; ++index ) {
      const light = lights[ index ];
      const lightName = 'uLights[' + index + '].';

      cameraMatrix.transformPoint( light.x, light.y, point );

      renderer.setFloat2( program, lightName + 'position',
        point.x - ( camera.scrollX * light.scrollFactorX * camera.zoom ),
        height - ( point.y - ( camera.scrollY * light.scrollFactorY ) *
        camera.zoom )
      );
      renderer.setFloat3( program, lightName + 'color',
        light.r, light.g, light.b );
      renderer.setFloat1( program, lightName + 'intensity', light.intensity );
      renderer.setFloat1( program, lightName + 'radius', light.radius );

      if ( light.hWidth && light.hHeight ) {
        renderer.setFloat1( program, lightName + 'hWidth', light.hWidth );
        renderer.setFloat1( program, lightName + 'hHeight', light.hHeight );
      }
    }

    return this;
  }

  /**
   * @override
   */
  batchTexture(
    gameObject,
    texture,
    textureWidth, textureHeight,
    srcX, srcY,
    srcWidth, srcHeight,
    scaleX, scaleY,
    rotation,
    flipX, flipY,
    scrollFactorX, scrollFactorY,
    displayOriginX, displayOriginY,
    frameX, frameY, frameWidth, frameHeight,
    tintTL, tintTR, tintBL, tintBR, tintEffect,
    uOffset, vOffset,
    camera,
    parentTransformMatrix
  ) {
    if ( !this.active ) {
      return;
    }

    this.renderer.setPipeline( this );

    let normalTexture;

    if ( gameObject.displayTexture ) {
      normalTexture = gameObject.displayTexture
        .dataSource[ gameObject.displayFrame.sourceIndex ];
    }
    else if ( gameObject.texture ) {
      normalTexture = gameObject.texture
        .dataSource[ gameObject.frame.sourceIndex ];
    }
    else if ( gameObject.tileset ) {
      // NOTE: PATCH HERE
      normalTexture = gameObject.tileset[ 0 ].image.dataSource[ 0 ];
    }

    if ( !normalTexture ) {
      console.warn( 'Normal map missing or invalid' );
      return;
    }

    this.setTexture2D( normalTexture.glTexture, 1 );

    const camMatrix = this._tempMatrix1;
    const spriteMatrix = this._tempMatrix2;
    const calcMatrix = this._tempMatrix3;

    let u0 = ( frameX / textureWidth ) + uOffset;
    let v0 = ( frameY / textureHeight ) + vOffset;
    let u1 = ( frameX + frameWidth ) / textureWidth + uOffset;
    let v1 = ( frameY + frameHeight ) / textureHeight + vOffset;

    let width = srcWidth;
    let height = srcHeight;

    // var x = -displayOriginX + frameX;
    // var y = -displayOriginY + frameY;

    let x = -displayOriginX;
    let y = -displayOriginY;

    if ( gameObject.isCropped ) {
      const crop = gameObject._crop;

      width = crop.width;
      height = crop.height;

      srcWidth = crop.width;
      srcHeight = crop.height;

      frameX = crop.x;
      frameY = crop.y;

      let ox = frameX;
      let oy = frameY;

      if ( flipX ) {
        ox = ( frameWidth - crop.x - crop.width );
      }

      if ( flipY && !texture.isRenderTexture ) {
        oy = ( frameHeight - crop.y - crop.height );
      }

      u0 = ( ox / textureWidth ) + uOffset;
      v0 = ( oy / textureHeight ) + vOffset;
      u1 = ( ox + crop.width ) / textureWidth + uOffset;
      v1 = ( oy + crop.height ) / textureHeight + vOffset;

      x = -displayOriginX + frameX;
      y = -displayOriginY + frameY;
    }

    // Invert the flipY if this is a RenderTexture
    flipY = flipY ^ ( texture.isRenderTexture ? 1 : 0 );

    if ( flipX ) {
      width *= -1;
      x += srcWidth;
    }

    if ( flipY ) {
      height *= -1;
      y += srcHeight;
    }

    const xw = x + width;
    const yh = y + height;

    spriteMatrix.applyITRS( srcX, srcY, rotation, scaleX, scaleY );

    camMatrix.copyFrom( camera.matrix );

    if ( parentTransformMatrix ) {
      // Multiply the camera by the parent matrix
      camMatrix.multiplyWithOffset( parentTransformMatrix,
        -camera.scrollX * scrollFactorX, -camera.scrollY * scrollFactorY );

      // Undo the camera scroll
      spriteMatrix.e = srcX;
      spriteMatrix.f = srcY;

      // Multiply by the Sprite matrix, store result in calcMatrix
      camMatrix.multiply( spriteMatrix, calcMatrix );
    }
    else {
      spriteMatrix.e -= camera.scrollX * scrollFactorX;
      spriteMatrix.f -= camera.scrollY * scrollFactorY;

      // Multiply by the Sprite matrix, store result in calcMatrix
      camMatrix.multiply( spriteMatrix, calcMatrix );
    }

    let tx0 = calcMatrix.getX( x, y );
    let ty0 = calcMatrix.getY( x, y );

    let tx1 = calcMatrix.getX( x, yh );
    let ty1 = calcMatrix.getY( x, yh );

    let tx2 = calcMatrix.getX( xw, yh );
    let ty2 = calcMatrix.getY( xw, yh );

    let tx3 = calcMatrix.getX( xw, y );
    let ty3 = calcMatrix.getY( xw, y );

    if ( camera.roundPixels ) {
      tx0 |= 0;
      ty0 |= 0;

      tx1 |= 0;
      ty1 |= 0;

      tx2 |= 0;
      ty2 |= 0;

      tx3 |= 0;
      ty3 |= 0;
    }

    this.setTexture2D( texture, 0 );

    this.batchQuad( tx0, ty0, tx1, ty1, tx2, ty2, tx3, ty3, u0, v0, u1, v1,
      tintTL, tintTR, tintBL, tintBR, tintEffect );
  }
}

import seedrandom from 'seedrandom';

/**
 * A utility class that provides functions for random number generation based on
 * a given seed value
 */
export default class Random {
  /**
   * @param {number} seedValue The seed value for RNG
   */
  constructor( seedValue ) {
    /**
     * The random number generator used for this instance
     * @type {function}
     */
    this.rng = seedrandom( seedValue );
  }

  /**
   * Gets a random integer
   * @param {number} min The minimum value the integer can be
   * @param {number} max The maximum value the integer can be
   * @param {boolean|undefined} even True = Only Even, False = Only Odd,
   *          Undefined = Odd and Even
   * @return {number} A random integer
   */
  randInt( min, max, even = undefined ) {
    if ( even === true ) {
      return this.randEvenInt( min, max );
    }
    else if ( even === false ) {
      return this.randOddInt( min, max );
    }
    return Math.floor( this.rng() * ( max - min + 1 ) + min );
  }

  /**
   * Gets a random even integer
   * @param {number} min The minimum value the integer can be
   * @param {number} max The maximum value the integer can be
   * @return {number} A random even integer
   */
  randEvenInt( min, max ) {
    if ( min % 2 !== 0 && min < max ) {
      min++;
    }
    if ( max % 2 !== 0 && max > min ) {
      max--;
    }
    const range = ( max - min ) / 2;
    return Math.floor( this.rng() * ( range + 1 ) ) * 2 + min;
  }

  /**
   * Gets a random odd integer
   * @param {number} min The minimum value the integer can be
   * @param {number} max The maximum value the integer can be
   * @return {number} A random odd integer
   */
  randOddInt( min, max ) {
    if ( min % 2 === 0 ) {
      min++;
    }
    if ( max % 2 === 0 ) {
      max--;
    }
    const range = ( max - min ) / 2;
    return Math.floor( this.rng() * ( range - 1 ) ) * 2 + min;
  }

  /**
   * Gets a random direction (North, East, South, West)
   * @return {number} A random direction
   */
  randDirection() {
    return this.randInt( 0, 3 );
  }

  /**
   * Gets a random value in the given array
   * @param {Array} array The array to choose from
   * @return {*} The chosen value of the array
   */
  randPick( array ) {
    return array[ this.randInt( 0, array.length - 1 ) ];
  }
}

